    public boolean handle(SocketChannel ch, ByteBuffer buf) {
        if (buf.remaining() == 0) {
            buf.clear();
        }
        if (ch.finishConnect()) {
            return true;
        }
        return true;
    }

    public void stop() {
        running = false;
        try {
            selector.close();
            ch.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

