            } catch (IOException e) {
                log.severe("Error closing selector: " + e.getMessage());
            }
        }
        if (interrupt) {
            Thread.currentThread().interrupt();
        }
        log.info("Server shutdown complete.");
    }

    @Override
    public void run() {
        while (!isInterrupted()) {
            try {
                selector.select();
                for (SelectionKey key : selector.selectedKeys()) {
