    public CompletableFuture<Integer> start(int port) {
        CompletableFuture<Integer> future = new CompletableFuture<>();
        bootstrap.bind(port).addListener((GenericFutureListener<Future<? super Void>>) future1 -> {
            if (future1.isSuccess()) {
                int boundPort = ((ChannelFuture) future1).channel().localAddress().getPort();
                allChannels.add(((ChannelFuture) future1).channel());
                future.complete(boundPort);
            } else {
                future.completeExceptionally(future1.cause());
