                } else {
                    future.completeExceptionally(future1.cause());
                }
            });
        } else {
            releaseSslContext();
            future.complete(null);
        }
        return future;
    }

    private void releaseSslContext() {
        if (sslContext != null && hasReleasedSslContext.compareAndSet(false, true)) {
            sslContext.release();
        }
    }
}

