import java.nio.file.Path;
import java.util.function.Consumer;

public class RandomAccessReadMemoryMappedFile {
    private final ByteBuffer mappedByteBuffer;
    private final long size;
    private final FileChannel fileChannel;
    private final Consumer<? super ByteBuffer> unmapper;
    private long position;

    public RandomAccessReadMemoryMappedFile(String filename) throws IOException {
        this(new File(filename));
    }

    public RandomAccessReadMemoryMappedFile(File file) throws IOException {
        this(file.toPath());
    }

    public RandomAccessReadMemoryMappedFile(Path path) throws IOException {
        fileChannel = FileChannel.open(path, java.nio.file.StandardOpenOption.READ);
        size = fileChannel.size();
        mappedByteBuffer = fileChannel.map(FileChannel.MapMode.READ_ONLY, 0, size);
        unmapper = (buffer) -> {
            try {
                if (fileChannel != null) {
                    fileChannel.close();
                }
            } catch (IOException e) {
                throw new RuntimeException(e);
            }
        };
        position = 0;
    }

    public void close() {
        if (fileChannel != null) {
            try {
                fileChannel.close();
            } catch (IOException e) {
                throw new RuntimeException(e);
            }
        }
        unmapper.accept(mappedByteBuffer);
    }

    public void seek(long position) {
        checkClosed();
        this.position = position;
    }

    public long getPosition() {
        return position;
    }

    public int read() {
        checkClosed();
        if (isEOF()) {
            return -1;
        }
        int b = mappedByteBuffer.get((int) position) & 0xFF;
        position++;
        return b;
    }

    public int read(byte[] b, int offset, int length) {
        checkClosed();
        if (isEOF()) {
            return -1;
        }
        int bytesRead = Math.min(length, (int) (size - position));
        mappedByteBuffer.position((int) position);
        mappedByteBuffer.get(b, offset, bytesRead);
        position += bytesRead;
        return bytesRead;
    }

    public long length() {
        return size;
    }

    public boolean isClosed() {
        try {
            return fileChannel == null || fileChannel.isOpen();
        } catch (IOException e) {
            return true;
        }
    }

    public boolean isEOF() {
        return position >= size;
    }

    private void checkClosed() {
        if (isClosed()) {
            throw new IllegalStateException("Reader is closed");
        }
    }
}

