    protected static final String SH_SHELL = "/bin/sh";
    protected static final String CMD_SHELL = "cmd.exe";

    public ProcessResult executeScript(TaskContext context, String scriptPath) {
        if (context == null || scriptPath == null || scriptPath.isEmpty()) {
            return new ProcessResult(false, "Invalid context or script path");
        }

        String shellCommand = getRunCommand();
        if (shellCommand == null || shellCommand.isEmpty()) {
            return new ProcessResult(false, "Shell command not provided by subclass");
        }

        ProcessBuilder processBuilder = new ProcessBuilder(shellCommand, scriptPath);
        processBuilder.redirectErrorStream(false);
        try {
            Process process = processBuilder.start();

            StringBuilder stdout = new StringBuilder();
            StringBuilder stderr = new StringBuilder();

            ExecutorService executor = Executors.newFixedThreadPool(2);
            executor.submit(() -> copyStream(process.getInputStream(), stdout, context.getLogger(), getCharset()));
            executor.submit(() -> copyStream(process.getErrorStream(), stderr, context.getLogger(), getCharset()));
            executor.shutdown();

            int exitCode = process.waitFor();

            if (exitCode != 0) {
                return new ProcessResult(false, "Script execution failed with exit code: " + exitCode, stdout.toString(), stderr.toString());
            }

            return new ProcessResult(true, "Script executed successfully", stdout.toString(), stderr.toString());
        } catch (Exception e) {
            return new ProcessResult(false, "Error executing script: " + e.getMessage());
        }
    }

    protected void copyStream(InputStream is, StringBuilder sb, OmsLogger omsLogger, Charset charset) {
        try (BufferedReader reader = new BufferedReader(new InputStreamReader(is, charset))) {
