static int runAppProcess(
    ProcessBuilder pb,
    Logger logger,
    Reader appInput,
    Writer appOutput) throws IOException, InterruptedException {
  pb.redirectErrorStream(true);
  if (logger != null) {
    logger.info("start process: " + pb.command());
  }
  Process p = pb.start();

  if (appInput != null) {
    OutputStream out =
        new BufferedOutputStream(
            p.getOutputStream(),
            100 * 1024);
    int c;
    while ((c = appInput.read()) != -1) {
      out.write(c);
    }
    out.flush();
  }
  if (appOutput != null) {
    InputStream in =
        new BufferedInputStream(
            p.getInputStream(),
            100 * 1024);
    int c;
    while ((c = in.read()) != -1) {
      appOutput.write(c);
    }
    appOutput.flush();
    in.close();
  }
  p.waitFor();

  int status = p.exitValue();
  if (logger != null) {
    logger.info("exit status=" + status + " from " + pb.command());
  }
  return status;
}

