public class ShellCommand extends AbstractCommand {
    private String command;
    private List<String> argv;
    private Pattern shellWildcardPattern;

    public ShellCommand(String command) {
        this.command = command;
        this.shellWildcardPattern = Pattern.compile("[*?[\$$]");
        this.argv = tokenize(command);
    }

    public boolean hasWildcard(String command) {
        return shellWildcardPattern.matcher(command).find();
    }

    public List<String> tokenize(String s) {
        List<String> tokens = new ArrayList<>();
        StringBuilder token = new StringBuilder();
        boolean inQuotes = false;
        for (int i = 0; i < s.length(); i++) {
            char c = s.charAt(i);
            if (c == '"') {
                inQuotes = !inQuotes;
            } else if (!inQuotes && c == ' ') {
                if (token.length() > 0) {
                    tokens.add(token.toString());
                    token.setLength(0);
                }
            } else {
                token.append(c);
            }
        }
        if (token.length() > 0) {
            tokens.add(token.toString());
        }
        return tokens;
    }

    public void doExecute(ConcurrentTestCommandExecutor executor) {
        try {
            ProcessBuilder processBuilder;
            if (hasWildcard(command)) {
                processBuilder = new ProcessBuilder("/bin/sh", "-c", command);
            } else {
                processBuilder = new ProcessBuilder(argv);
            }
            processBuilder.redirectErrorStream(true);
            Process process = processBuilder.start();
            BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()));
            String line;
            while ((line = reader.readLine()) != null) {
                executor.reportOutput(line);
            }
            int exitCode = process.waitFor();
            executor.reportStatus(exitCode == 0 ? "Success" : "Failure");
        } catch (IOException | InterruptedException e) {
            executor.reportStatus("Error: " + e.getMessage());
        }
    }
}

