public class ProcessExecutor {
    private final ProcessBuilder mProcessBuilder;
    private Process mProcess;
    private final ThreadPoolExecutor mExecutor;

    public ProcessExecutor(Path scriptPath, Path libraryPath) {
        mProcessBuilder = new ProcessBuilder(scriptPath.toString(), libraryPath.toString());
        mExecutor = (ThreadPoolExecutor) Executors.newSingleThreadExecutor();
    }

    public void execute(ProcessOutputHandler outputHandler) {
        mExecutor.execute(() -> {
            try {
                mProcess = mProcessBuilder.start();
                StreamGobbler outputGobbler = new StreamGobbler(mProcess.getInputStream(), outputHandler::handleOutput);
                StreamGobbler errorGobbler = new StreamGobbler(mProcess.getErrorStream(), outputHandler::handleError);

                outputGobbler.start();
                errorGobbler.start();

                int exitCode = mProcess.waitFor();
                outputHandler.handleCompletion(exitCode);
            } catch (Exception e) {
                outputHandler.handleError(e.getMessage());
            }
        });
    }

    public void terminate() {
        if (mProcess != null) {
            mProcess.destroy();
            try {
                mProcess.waitFor();
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        }
        mExecutor.shutdownNow();
    }

    private static class StreamGobbler extends Thread {
        private final InputStream inputStream;
        private final ProcessOutputHandler handler;

        public StreamGobbler(InputStream inputStream, ProcessOutputHandler handler) {
            this.inputStream = inputStream;
            this.handler = handler;
        }

        @Override
        public void run() {
            try (BufferedReader reader = new BufferedReader(new InputStreamReader(inputStream))) {
                String line;
                while ((line = reader.readLine()) != null) {
                    handler.handleOutput(line);
                }
            } catch (IOException e) {
                handler.handleError(e.getMessage());
            }
        }
