    private final Map<String, DaemonInfo> infosMap = new HashMap<>();

    public DaemonRegistry(Path registryFile) throws IOException {
        this.registryFile = registryFile;
        if (!Files.exists(registryFile)) {
            Files.createFile(registryFile);
        }
        initChannel();
        read();
    }

    private void initChannel() throws IOException {
        synchronized (lck) {
            channel = FileChannel.open(registryFile, StandardOpenOption.READ, StandardOpenOption.WRITE);
            size = Math.max(channel.size(), MAX_LENGTH);
            resizeBuffer(size);
        }
    }

    private void resizeBuffer(long newSize) throws IOException {
        synchronized (lck) {
            if (newSize <= size) {
                return;
            }
            long nextSize = nextPowerOf2(newSize, MAX_LENGTH);
            if (nextSize > size) {
                try {
                    channel.truncate(nextSize);
                    buffer = channel.map(FileChannel.MapMode.READ_WRITE, 0, nextSize);
                    size = nextSize;
                } catch (IOException e) {
                    LOGGER.severe("Failed to resize buffer: " + e.getMessage());
                    throw e;
                }
            }
        }
    }

    private long nextPowerOf2(long a, long min) {
        long result = 1;
        while (result < a && result < min) {
            result <<= 1;
        }
        return result;
    }

    @Override
    public void close() throws IOException {
        synchronized (lck) {
            if (channel != null) {
                channel.close();
                channel = null;
                buffer = null;
            }
        }
    }

    public List<DaemonInfo> getAll() {
        synchronized (infosMap) {
            return new ArrayList<>(infosMap.values());
        }
    }

    private void read() {
        synchronized (lck) {
            try {
                buffer.position(0);
                int count = buffer.getInt();
                for (int i = 0; i < count; i++) {
                    String id = readString();
                    DaemonInfo info = new DaemonInfo();
                    info.setId(id);
                    info.setActive(buffer.getBoolean());
                    info.setTimestamp(buffer.getLong());
                    info.setStatus(buffer.getInt());
                    info.setData(readString());
                    infosMap.put(id, info);
                }
            } catch (Exception e) {
                LOGGER.severe("Error reading from buffer: " + e.getMessage());
                reset();
            }
        }
    }

    public void doUpdate(Runnable updater) {
        synchronized (lck) {
            FileLock lock = null;
            try {
                lock = tryLock();
                if (lock == null) {
                    return;
                }
                updater.run();
            } catch (Exception e) {
                LOGGER.severe("Update failed: " + e.getMessage());
            } finally {
                if (lock != null) {
                    try {
                        lock.release();
                    } catch (IOException e) {
                        LOGGER.severe("Failed to release lock: " + e.getMessage());
                    }
                }
            }
        }
    }

    private FileLock tryLock() {
        try {
            return channel.tryLock();
        } catch (IOException e) {
            LOGGER.severe("Failed to acquire lock: " + e.getMessage());
            return null;
        }
    }

    public void reset() {
        synchronized (lck) {
            synchronized (infosMap) {
                infosMap.clear();
                buffer.position(0);
                buffer.putInt(0);
            }
        }
    }

    private String readString() {
        int length = buffer.getInt();
        if (length == 0) {
            return "";
        }
        byte[] bytes = new byte[length];
        buffer.get(bytes);
        return new String(bytes);
    }

    private void writeString(String str) {
        byte[] bytes = str.getBytes();
        buffer.putInt(bytes.length);
        buffer.put(bytes);
    }

    public static class DaemonInfo {
        private String id;
        private boolean active;
        private long timestamp;
        private int status;
        private String data;

        public String getId() {
            return id;
        }

        public void setId(String id) {
            this.id = id;
        }

        public boolean isActive() {
            return active;
        }

        public void setActive(boolean active) {
            this.active = active;
        }

        public long getTimestamp() {
            return timestamp;
        }

        public void setTimestamp(long timestamp) {
            this.timestamp = timestamp;
        }

        public int getStatus() {
            return status;
        }

        public void setStatus(int status) {
            this.status = status;
        }

        public String getData() {
            return data;
        }

        public void setData(String data) {
            this.data = data;
        }
    }
}

