import java.io.File;
import java.io.IOException;
import java.util.Map;
import java.util.concurrent.TimeUnit;

public class ShellUtils {
    private static final String[] BINARY_PLACES = {"/system/bin", "/system/xbin", "/data/local/tmp", "/sbin"};
    private static final String PYTHON_HOME = "/system/usr/bin";
    private static final File DATA_DIR = new File("/data/data/");
    private static final File BUSYBOX_FILE = new File("/system/xbin/busybox");
    private static Boolean IS_ROOTED = null;

    public static Process executeNoWait(Map<String, String> env, String... command) throws IOException {
        ProcessBuilder processBuilder = new ProcessBuilder(command);
        if (env != null) {
            processBuilder.environment().putAll(env);
        }
        return processBuilder.start();
    }

    public static Process sudoNoWait(Map<String, String> env, String... command) throws IOException {
        ProcessBuilder processBuilder = new ProcessBuilder("su", "-c", String.join(" ", command));
        if (env != null) {
            processBuilder.environment().putAll(env);
        }
        return processBuilder.start();
    }

    public static String waitFor(String command, Process process) throws IOException, InterruptedException {
        int exitCode = process.waitFor();
        if (exitCode != 0) {
            throw new RuntimeException("Command failed: " + command + ", exit code: " + exitCode);
        }
        StringBuilder output = new StringBuilder();
        java.io.InputStream inputStream = process.getInputStream();
        byte[] buffer = new byte[1024];
        int length;
        while ((length = inputStream.read(buffer)) != -1) {
            output.append(new String(buffer, 0, length));
        }
        return output.toString();
    }

    public static String findCommand(String command) {
        for (String place : BINARY_PLACES) {
            File file = new File(place, command);
            if (file.exists() && file.canExecute()) {
                return file.getAbsolutePath();
            }
        }
        return null;
    }

    public static boolean isRooted() {
        if (IS_ROOTED == null) {
            try {
                Process process = new ProcessBuilder("su", "-v").start();
                IS_ROOTED = process.waitFor(5, TimeUnit.SECONDS) == 0;
            } catch (Exception e) {
                IS_ROOTED = false;
            }
        }
        return IS_ROOTED;
    }
}

