import eu.chainfire.libsuperuser.Shell;

public class ProcessHandler {

    private static final String TAG = "ProcessHandler";
    private final Context mContext;
    private final boolean mUseRoot;
    @Inject SharedPreferences mPreferences;

    public ProcessHandler(Context context) {
        mContext = context;
        mUseRoot = mPreferences.getBoolean(Constants.PREF_USE_ROOT, false) && Shell.SU.available();
    }

    public Process setupAndLaunch(HashMap<String, String> env, String[] command) throws IOException {
        if (mUseRoot) {
            ProcessBuilder pb = new ProcessBuilder("su");
            Process process = pb.start();
            DataOutputStream suOut = new DataOutputStream(process.getOutputStream());
            for (Map.Entry<String, String> entry : env.entrySet()) {
                suOut.writeBytes(String.format("export %s=\"%s\"\n", entry.getKey(), entry.getValue()));
            }
            suOut.flush();
            suOut.writeBytes("exec " + TextUtils.join(" ", command) + "\n");
            suOut.flush();
            return process;
        } else {
            ProcessBuilder pb = new ProcessBuilder(command);
            pb.environment().putAll(env);
            return pb.start();
        }
    }

    public Thread log(final InputStream is, final int priority) {
        Thread t = new Thread(() -> {
            BufferedReader br = null;
            try {
                br = new BufferedReader(new InputStreamReader(is, Charsets.UTF_8));
                String line;
                while ((line = br.readLine()) != null) {
                    Log.println(priority, TAG, line);
                }
            } catch (IOException e) {
                Log.w(TAG, "Failed to read process output", e);
            } finally {
                if (br != null) {
                    try {
                        br.close();
                    } catch (IOException e) {
                        Log.w(TAG, "Failed to close bufferedReader", e);
                    }
                }
            }
        });
        t.start();
        return t;
    }

    @SuppressLint("WakelockTimeout")
    public String runProcess(ProcessBuilder pb, boolean returnStdOut) {
        String capturedStdOut = "";
        PowerManager pm = (PowerManager) mContext.getSystemService(Context.POWER_SERVICE);
        PowerManager.WakeLock wakeLock = pm.newWakeLock(PowerManager.PARTIAL_WAKE_LOCK, TAG);
        try {
            wakeLock.acquire();
            Process process = pb.start();

            Thread lInfo = null;
            Thread lWarn = null;
            if (returnStdOut) {
                BufferedReader br = new BufferedReader(new InputStreamReader(process.getInputStream(), Charsets.UTF_8));
                String line;
                while ((line = br.readLine()) != null) {
                    Log.println(Log.INFO, TAG, line);
                    capturedStdOut = capturedStdOut + line + "\n";
                }
                br.close();
            } else {
                lInfo = log(process.getInputStream(), Log.INFO);
                lWarn = log(process.getErrorStream(), Log.WARN);
            }

            int ret = process.waitFor();
            Log.i(TAG, "Process exited with code " + ret);
            if (lInfo != null) lInfo.join();
            if (lWarn != null) lWarn.join();
        } catch (IOException | InterruptedException e) {
            Log.e(TAG, "Failed to execute process or read output", e);
        } finally {
            wakeLock.release();
        }
        return capturedStdOut;
    }

    public List<String> getProcessPIDs(String processName) {
        List<String> pids = new ArrayList<>();
        Process ps = null;
        DataOutputStream psOut = null;
        BufferedReader br = null;
        try {
            ps = Runtime.getRuntime().exec((mUseRoot) ? "su" : "sh");
            psOut = new DataOutputStream(ps.getOutputStream());
            psOut.writeBytes("ps\n");
            psOut.writeBytes("exit\n");
            psOut.flush();
            ps.waitFor();
            br = new BufferedReader(new InputStreamReader(ps.getInputStream(), "UTF-8"));
            String line;
            while ((line = br.readLine()) != null) {
                if (line.contains(processName)) {
                    String pid = line.trim().split("\\s+")[1];
                    Log.v(TAG, "getProcessPIDs: Found process PID [" + pid + "]");
                    pids.add(pid);
                }
            }
        } catch (IOException | InterruptedException e) {
            Log.w(TAG, "Failed to list processes", e);
        } finally {
            try {
                if (br != null) br.close();
                if (psOut != null) psOut.close();
            } catch (IOException e) {
                Log.w(TAG, "Failed to close streams", e);
            }
            if (ps != null) ps.destroy();
        }
        return pids;
    }
}

