        if (env != null) {
            pb.environment().putAll(env);
        }
        pb.redirectErrorStream(true);
        Process process = pb.start();
        log(process.getInputStream(), Log.INFO);
        return process;
    }

    public Thread log(InputStream is, int priority) {
        Thread thread = new Thread(() -> {
            try (BufferedReader reader = new BufferedReader(new InputStreamReader(is))) {
                String line;
                while ((line = reader.readLine()) != null) {
                    Log.println(priority, "ProcessHandler", line);
                }
            } catch (IOException e) {
                Log.e("ProcessHandler", "Error reading stream", e);
            }
        });
        thread.start();
        return thread;
    }

    public String runProcess(ProcessBuilder pb, boolean returnStdOut) throws IOException, InterruptedException {
        PowerManager pm = (PowerManager) mContext.getSystemService(Context.POWER_SERVICE);
        PowerManager.WakeLock wakeLock = pm.newWakeLock(PowerManager.PARTIAL_WAKE_LOCK, "ProcessHandler::WakeLock");
        wakeLock.acquire();

        Process process = pb.start();
        log(process.getInputStream(), Log.INFO);

        int exitCode = process.waitFor();
        wakeLock.release();

        if (returnStdOut) {
            try (BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()))) {
                StringBuilder output = new StringBuilder();
                String line;
                while ((line = reader.readLine()) != null) {
                    output.append(line).append("\n");
                }
                return output.toString();
            }
        }
        return "";
    }

    public List<String> getProcessPIDs(String processName) {
        List<String> pids = new ArrayList<>();
        ActivityManager am = (ActivityManager) mContext.getSystemService(Context.ACTIVITY_SERVICE);
        List<RunningAppProcessInfo> processes = am.getRunningAppProcesses();

        for (RunningAppProcessInfo process : processes) {
            if (process.processName.equals(processName)) {
                pids.add(String.valueOf(process.pid));
            }
        }

        return pids;
    }
}

