    private int port;
    private String username;
    private String password;
    private String queueName;
    private boolean durable;
    private Connection connection;
    private Channel channel;

    public RabbitMQProducer(Map<String, String> config) {
        this.host = config.get("rabbitmq_host");
        this.port = Integer.parseInt(config.get("rabbitmq_port"));
        this.username = config.get("rabbitmq_user");
        this.password = config.get("rabbitmq_pass");
        this.queueName = config.get("rabbitmq_queuename");
        this.durable = config.get("rabbitmq_durable").equals("true");
    }

