import io.lettuce.core.RedisClient;
import io.lettuce.core.api.StatefulRedisConnection;
import io.lettuce.core.api.sync.SyncCommands;
import reactor.core.publisher.Flux;
import reactor.core.publisher.Publisher;
import reactor.core.publisher.SynchronousSink;

public class RedisPublisher {
    private final RedisClient client;
    private final StatefulRedisConnection<String, String> connection;

    public RedisPublisher() {
        client = RedisClient.create("redis://localhost:6379");
        connection = client.connect();
        SyncCommands<String, String> syncCommands = connection.sync();
        syncCommands.flushall();
    }

    public Publisher<String> createPublisher(long elements) {
        if (elements == Long.MAX_VALUE) {
            return null;
        }
        String listKey = "reactive_list";
        SyncCommands<String, String> syncCommands = connection.sync();
        for (long i = 0; i < elements; i++) {
            syncCommands.rpush(listKey, "element-" + i);
        }

        return Flux.generate(() -> 0L, (count, sink) -> {
            long start = count;
            long end = Math.min(count + 1000, elements - 1);
            SyncCommands<String, String> commands = connection.sync();
            List<String> batch = commands.lrange(listKey, start, end);
            for (String item : batch) {
                sink.next(item);
