public class ClusterConnectionProvider {
    private final JedisClusterInfoCache cache;
    private final JedisClientConfig clientConfig;
    private final Map<String, ConnectionPool> connectionMap = new ConcurrentHashMap<>();
    private final AtomicReference<Set<HostAndPort>> seedNodes = new AtomicReference<>();

    public ClusterConnectionProvider(Set<HostAndPort> seedNodes, JedisClientConfig clientConfig) {
        this.seedNodes.set(seedNodes);
        this.clientConfig = clientConfig;
        this.cache = new JedisClusterInfoCache();
        initializeSlotsCache(seedNodes, clientConfig);
    }

    public void initializeSlotsCache(Set<HostAndPort> seedNodes, JedisClientConfig clientConfig) {
        this.seedNodes.set(seedNodes);
        this.cache.updateClusterNodes(seedNodes, clientConfig);
    }

    public Connection getConnection(CommandArguments args) {
        int slot = getSlot(args);
        return getConnectionFromSlot(slot);
    }

    public Connection getConnection() {
        if (connectionMap.isEmpty()) {
            throw new JedisConnectionException("No available connections in the cluster.");
        }
        return getConnectionFromRandomNode();
    }

    public Connection getConnectionFromSlot(int slot) {
        Node node = cache.getSlotNode(slot);
        if (node == null) {
            renewSlotCache();
            node = cache.getSlotNode(slot);
        }
        if (node == null) {
            throw new JedisConnectionException("No node found for slot: " + slot);
        }
        return getConnectionFromNode(node.getHost(), node.getPort());
    }

    public void renewSlotCache() {
        Set<HostAndPort> currentSeedNodes = seedNodes.get();
        if (currentSeedNodes != null && !currentSeedNodes.isEmpty()) {
            cache.updateClusterNodes(currentSeedNodes, clientConfig);
        }
    }

    public void close() {
        connectionMap.forEach((key, pool) -> {
            if (pool != null) {
                pool.close();
            }
        });
        connectionMap.clear();
    }

    public Map<String, ConnectionPool> getConnectionMap() {
        return Collections.unmodifiableMap(connectionMap);
    }

    private int getSlot(CommandArguments args) {
        // Implement your slot calculation based on command arguments
        return Math.abs(args.toString().hashCode() % 16384);
    }

    private Connection getConnectionFromRandomNode() {
        if (connectionMap.isEmpty()) {
            throw new JedisConnectionException("No available connections in the cluster.");
        }
        String randomKey = new Random().nextInt(connectionMap.size()) + "";
        ConnectionPool pool = connectionMap.get(randomKey);
        if (pool == null || pool.isClosed()) {
            throw new JedisConnectionException("No available connection pool for random node selection.");
        }
        return pool.getResource();
    }

    private Connection getConnectionFromNode(String host, int port) {
        String key = host + ":" + port;
        ConnectionPool pool = connectionMap.get(key);
        if (pool == null || pool.isClosed()) {
            JedisPoolConfig poolConfig = new JedisPoolConfig();
            pool = new JedisPool(poolConfig, host, port, clientConfig);
            connectionMap.put(key, pool);
        }
        return pool.getResource();
    }

    private static class ConnectionPool extends JedisPool {
        public ConnectionPool(JedisPoolConfig poolConfig, String host, int port, JedisClientConfig clientConfig) {
            super(poolConfig, host, port, clientConfig);
        }
    }

