import java.util.concurrent.CompletableFuture;

public class RedisSentinelConnector {
    private final ClientResources clientResources;

    public RedisSentinelConnector(ClientResources clientResources) {
        this.clientResources = clientResources;
    }

    public StatefulRedisSentinelConnection<String, String> connectToSentinelDirectly(String host) {
        RedisClient client = RedisClient.create(clientResources);
        RedisURI uri = RedisURI.Builder.sentinel(host, 26379).build();
        return client.connectSentinel(uri);
    }

    public StatefulRedisConnection<String, String> connectToMasterViaSentinel(String host, String masterId, File truststore) throws Exception {
        SSLContext sslContext = setupSSLContext(truststore);
        RedisClient client = RedisClient.create(clientResources);
        RedisURI sentinelUri = RedisURI.Builder.sentinel(host, 26379)
                .ssl(true)
                .sslContext(sslContext)
                .build();

        StatefulRedisSentinelConnection<String, String> sentinelConnection = client.connectSentinel(sentinelUri);
        RedisSentinelCommands<String, String> sentinelCommands = sentinelConnection.sync();

        String masterAddress = sentinelCommands.getMasterAddrByName(masterId).get(0);

        String[] masterHostPort = masterAddress.split(":");
        RedisURI masterUri = RedisURI.Builder.redis(masterHostPort[0], Integer.parseInt(masterHostPort[1]))
                .ssl(true)
                .sslContext(sslContext)
                .build();

        return client.connect(masterUri);
    }

    private SSLContext setupSSLContext(File truststore) throws Exception {
        TrustManagerFactory tmf = TrustManagerFactory.getInstance(TrustManagerFactory.getDefaultAlgorithm());
        KeyStore ts = KeyStore.getInstance(KeyStore.getDefaultType());
        ts.load(null, null);
        try (var in = java.nio.file.Files.newInputStream(truststore.toPath())) {
            ts.load(in, "password".toCharArray());
        }
        tmf.init(ts);

        SSLContext sslContext = SSLContext.getInstance("TLS");
        sslContext.init(null, tmf.getTrustManagers(), null);
        return sslContext;
    }

    public void closeConnection(AutoCloseable connection, RedisClient client) {
        if (connection != null) {
            try {
                connection.close();
            } catch (Exception e) {
                // Handle exception
            }
        }
        if (client != null) {
            client.shutdown();
        }
    }
}

