        JedisPoolConfig poolConfig = new JedisPoolConfig();
        jedisPool = new JedisPool(poolConfig, host, port, 2000, password);
    }

    public void executeCommand(RedisCommand command) {
        if (isClosed) {
            throw new IllegalStateException("Redis connection pool is closed.");
        }
        try (Jedis jedis = jedisPool.getResource()) {
            switch (command.getCommandType()) {
                case SET:
                    jedis.set(command.getKey(), command.getValue());
                    break;
                case GET:
                    String value = jedis.get(command.getKey());
                    command.setResult(value);
                    break;
                case INCR:
                    Long incrResult = jedis.incr(command.getKey());
                    command.setResult(incrResult.toString());
                    break;
                default:
                    throw new IllegalArgumentException("Unsupported Redis command type: " + command.getCommandType());
            }
        }
    }

    public void close() {
        if (jedisPool != null) {
            jedisPool.close();
            isClosed = true;
        }
    }

    public boolean isClosed() {
        return isClosed;
    }
}

