public class FileChannelProcessor {
    private final FileChannel fileChannel;
    private final long mappedAddress;
    private final long fileSize;

    public FileChannelProcessor(String filePath) throws IOException {
        File file = new File(filePath);
        this.fileChannel = FileChannel.open(file.toPath(), FileChannel.openOptions().read());
        this.fileSize = file.length();
        MappedByteBuffer buffer = fileChannel.map(FileChannel.MapMode.READ_ONLY, 0, fileSize);
        this.mappedAddress = ((sun.nio.ch.DirectBuffer) buffer).address();
    }

    public long getMappedAddress() {
        return mappedAddress;
    }

    public long getFileSize() {
        return fileSize;
    }

    public void close() throws IOException {
        if (fileChannel != null) {
            fileChannel.close();
        }
    }
}

