public class MultiClusterPooledConnectionProvider {
    private final Logger log;
    private final Map<Integer, Cluster> multiClusterMap;
    private volatile int activeMultiClusterIndex;
    private final Lock activeClusterIndexLock;
    private volatile boolean lastClusterCircuitBreakerForcedOpen;

    public MultiClusterPooledConnectionProvider(MultiClusterClientConfig multiClusterClientConfig) throws JedisValidationException {
        if (multiClusterClientConfig == null) {
            throw new JedisValidationException("Configuration cannot be null");
        }

        this.log = LoggerFactory.getLogger(MultiClusterPooledConnectionProvider.class);
        this.multiClusterMap = new ConcurrentHashMap<>();
        this.activeClusterIndexLock = new ReentrantLock();
        this.activeMultiClusterIndex = -1;
        this.lastClusterCircuitBreakerForcedOpen = false;

        for (int i = 0; i < multiClusterClientConfig.getClusters().size(); i++) {
            ClusterConfig clusterConfig = multiClusterClientConfig.getClusters().get(i);
            JedisPoolConfig poolConfig = new JedisPoolConfig();
            poolConfig.setMaxTotal(clusterConfig.getMaxConnections());
            poolConfig.setMaxIdle(clusterConfig.getMaxIdleConnections());
            poolConfig.setMinIdle(clusterConfig.getMinIdleConnections());
            poolConfig.setTestOnBorrow(clusterConfig.isTestOnBorrow());
            poolConfig.setTestWhileIdle(clusterConfig.isTestWhileIdle());

            JedisPool connectionPool = new JedisPool(poolConfig, clusterConfig.getHost(), clusterConfig.getPort(),
                    clusterConfig.getConnectionTimeout(), clusterConfig.getPassword(), clusterConfig.getDatabase());

            Cluster cluster = new Cluster(connectionPool);
            multiClusterMap.put(i, cluster);
        }

        // Set the initial active cluster index
        setActiveMultiClusterIndex(0);
    }

    public Connection getConnection() {
        int currentActiveIndex = activeMultiClusterIndex;
        if (currentActiveIndex == -1) {
            return null;
        }

        Cluster cluster = multiClusterMap.get(currentActiveIndex);
        if (cluster != null) {
            return cluster.getConnection();
        }
        return null;
    }

    public Connection getConnection(int multiClusterIndex) {
        Cluster cluster = multiClusterMap.get(multiClusterIndex);
        if (cluster != null) {
            return cluster.getConnection();
        }
        return null;
    }

    public void close() {
        if (activeMultiClusterIndex != -1) {
            Cluster cluster = multiClusterMap.get(activeMultiClusterIndex);
            if (cluster != null) {
                cluster.getConnectionPool().close();
            }
        }
    }

    private void setActiveMultiClusterIndex(int index) {
        activeClusterIndexLock.lock();
        try {
            activeMultiClusterIndex = index;
        } finally {
            activeClusterIndexLock.unlock();
        }
    }

    public static class Cluster {
        private final JedisPool connectionPool;

        public Cluster(JedisPool connectionPool) {
            this.connectionPool = connectionPool;
        }

        public Connection getConnection() {
            Jedis jedis = connectionPool.getResource();
            return new Connection(jedis);
        }

