import java.util.concurrent.locks.Lock;
import java.util.concurrent.locks.ReentrantLock;

public class JedisSentinelPool extends Pool<Jedis> {
    private static final org.slf4j.Logger LOG = org.slf4j.LoggerFactory.getLogger(JedisSentinelPool.class);
    protected final JedisFactory factory;
    protected final JedisClientConfig sentinelClientConfig;
    protected final Collection<MasterListener> masterListeners;
    protected volatile HostAndPort currentHostMaster;
    protected final Lock initPoolLock;

    public JedisSentinelPool(String masterName, Set<HostAndPort> sentinels, JedisFactory factory, JedisClientConfig clientConfig) {
        this.factory = factory;
        this.sentinelClientConfig = clientConfig;
        this.masterListeners = new HashSet<>();
        this.initPoolLock = new ReentrantLock();
        this.currentHostMaster = initSentinels(sentinels, masterName);
        initMaster(currentHostMaster);
    }

    protected void initMaster(HostAndPort hostAndPort) {
        JedisPoolConfig poolConfig = new JedisPoolConfig();
        poolConfig.setMaxTotal(100);
        poolConfig.setMaxIdle(50);
        poolConfig.setMinIdle(10);
        poolConfig.setTestOnBorrow(false);
        poolConfig.setTestWhileIdle(true);
        poolConfig.setTestOnReturn(false);
        poolConfig.setBlockWhenExhausted(true);
        setPool(new JedisPool(factory, hostAndPort.getHost(), hostAndPort.getPort(), Protocol.DEFAULT_TIMEOUT, hostAndPort.getHost(), hostAndPort.getPort(), poolConfig, clientConfig));
    }

    protected HostAndPort initSentinels(Set<HostAndPort> sentinels, String masterName) {
        for (HostAndPort sentinel : sentinels) {
            MasterListener listener = new MasterListener(masterName, sentinel.getHost(), sentinel.getPort());
            masterListeners.add(listener);
            new Thread(listener).start();
        }
        return getMasterFromSentinels(sentinels, masterName);
    }

    protected HostAndPort getMasterFromSentinels(Set<HostAndPort> sentinels, String masterName) {
        for (HostAndPort sentinel : sentinels) {
            try (Jedis jedis = new Jedis(sentinel.getHost(), sentinel.getPort(), Protocol.DEFAULT_TIMEOUT, sentinelClientConfig)) {
                List<String> master = jedis.sentinelGetMasterAddrByName(masterName);
                if (master != null && master.size() >= 2) {
                    return toHostAndPort(master);
                }
            } catch (JedisConnectionException e) {
                LOG.warn("Cannot connect to Sentinel at " + sentinel, e);
            }
        }
        throw new RuntimeException("No master found for " + masterName);
    }

    protected HostAndPort toHostAndPort(List<String> master) {
        return new HostAndPort(master.get(0), Integer.parseInt(master.get(1)));
    }

    @Override
    public Jedis getResource() {
        try {
            initPoolLock.lock();
            if (getPool() == null || getPool().isClosed()) {
                initMaster(currentHostMaster);
            }
        } finally {
            initPoolLock.unlock();
        }
        return super.getResource();
    }

    @Override
    public void returnResource(Jedis jedis) {
        super.returnResource(jedis);
    }

    protected class MasterListener implements Runnable {
        private final String masterName;
        private final String host;
        private final int port;
        private final long subscribeRetryWaitTimeMillis = 1000;
        private Jedis j;
        private final AtomicBoolean running = new AtomicBoolean(true);

        public MasterListener(String masterName, String host, int port) {
            this.masterName = masterName;
            this.host = host;
            this.port = port;
        }

        @Override
        public void run() {
            while (running.get()) {
                try {
                    j = new Jedis(host, port, Protocol.DEFAULT_TIMEOUT, sentinelClientConfig);
                    j.subscribe(new SentinelMasterListener(masterName) {
                        @Override
                        public void onMessage(String channel, String message) {
                            if ("+switch-master".equals(channel)) {
                                String[] parts = message.split(" ");
                                if (parts.length >= 6) {
                                    String oldMaster = parts[1];
                                    String newMasterHost = parts[3];
                                    int newMasterPort = Integer.parseInt(parts[4]);
                                    HostAndPort newMaster = new HostAndPort(newMasterHost, newMasterPort);
                                    if (!newMaster.equals(currentHostMaster)) {
                                        currentHostMaster = newMaster;
                                        initPoolLock.lock();
                                        try {
                                            if (getPool() != null && !getPool().isClosed()) {
                                                getPool().close();
                                            }
                                            initMaster(currentHostMaster);
                                        } finally {
                                            initPoolLock.unlock();
                                        }
                                    }
                                }
                            }
                        }
                    }, masterName);
                } catch (Exception e) {
                    LOG.warn("Error in Sentinel listener for " + masterName + " on " + host + ":" + port, e);
                    try {
                        Thread.sleep(subscribeRetryWaitTimeMillis);
                    } catch (InterruptedException ie) {
                        LOG.warn("Interrupted while waiting to retry Sentinel connection", ie);
                    }
                } finally {
                    if (j != null) {
                        j.close();
                    }
                }
            }
        }

        public void shutdown() {
            running.set(false);
            if (j != null) {
                j.quit();
                j.close();
            }
        }
    }
}

