import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import javax.sound.sampled.AudioFormat;

public class WaveWriter implements AutoCloseable {
    protected final AudioFormat mAudioFormat;
    protected int mFileRolloverCounter;
    protected final long mMaxSize;
    protected Path mFile;
    protected FileChannel mFileChannel;
    protected boolean mDataChunkOpen;
    protected long mDataChunkSizeOffset;
    protected int mDataChunkSize;

    public WaveWriter(AudioFormat format, Path file, long maxSize) {
        mAudioFormat = format;
        mFile = file;
        mMaxSize = maxSize;
        mFileRolloverCounter = 0;
        open();
    }

    public WaveWriter(AudioFormat format, Path file) {
        this(format, file, 0);
    }

    private void open() {
        try {
            mFileChannel = FileChannel.open(mFile, StandardOpenOption.CREATE_NEW, StandardOpenOption.WRITE);
            writeWaveHeader();
            openDataChunk();
        } catch (IOException e) {
            throw new RuntimeException("Failed to open file channel", e);
        }
    }

    @Override
    public void close() {
        close(null);
    }

    public void close(Path path) {
        if (mFileChannel != null) {
            try {
                closeDataChunk();
                updateTotalSize();
                mFileChannel.close();
                if (path != null) {
                    Files.move(mFile, path);
                }
            } catch (IOException e) {
                throw new RuntimeException("Failed to close file channel", e);
            }
        }
    }

    public void writeData(ByteBuffer buffer) {
        if (!mDataChunkOpen) {
            openDataChunk();
        }
        while (buffer.hasRemaining()) {
            int written = mFileChannel.write(buffer);
            if (written == 0) {
                rollover();
                continue;
            }
            mDataChunkSize += written;
            if (mMaxSize > 0 && mFileChannel.size() >= mMaxSize) {
                rollover();
            }
        }
    }

    private void closeDataChunk() {
        if (mDataChunkOpen) {
            try {
                updateDataChunkSize();
                mDataChunkOpen = false;
            } catch (IOException e) {
                throw new RuntimeException("Failed to close data chunk", e);
            }
        }
    }

    private void openDataChunk() {
        try {
            mDataChunkSize = 0;
            mDataChunkSizeOffset = mFileChannel.position();
            mFileChannel.write(getDataHeader());
            mDataChunkOpen = true;
        } catch (IOException e) {
            throw new RuntimeException("Failed to open data chunk", e);
        }
    }

    private void rollover() {
        closeDataChunk();
        close();
        updateFileName();
        open();
    }

    private void updateTotalSize() {
        try {
            ByteBuffer sizeBuffer = getUnsignedIntegerBuffer(mFileChannel.size());
            mFileChannel.position(4);
            mFileChannel.write(sizeBuffer);
        } catch (IOException e) {
            throw new RuntimeException("Failed to update total size", e);
        }
    }

    private void updateDataChunkSize() {
        try {
            ByteBuffer sizeBuffer = getUnsignedIntegerBuffer(mDataChunkSize);
            mFileChannel.position(mDataChunkSizeOffset);
            mFileChannel.write(sizeBuffer);
        } catch (IOException e) {
            throw new RuntimeException("Failed to update data chunk size", e);
        }
    }

    protected static ByteBuffer getUnsignedIntegerBuffer(long size) {
        ByteBuffer buffer = ByteBuffer.allocate(4);
        buffer.order(java.nio.ByteOrder.LITTLE_ENDIAN);
        buffer.putInt((int) size);
        return buffer;
    }

    private void updateFileName() {
        mFileRolloverCounter++;
        String baseName = mFile.getFileName().toString();
        int dotIndex = baseName.lastIndexOf('.');
        String namePart = dotIndex == -1 ? baseName : baseName.substring(0, dotIndex);
        String extPart = dotIndex == -1 ? "" : baseName.substring(dotIndex);
        mFile = mFile.getParent().resolve(namePart + "_" + mFileRolloverCounter + extPart);
    }

    public static ByteBuffer getDataHeader() {
        ByteBuffer buffer = ByteBuffer.allocate(8);
        buffer.order(java.nio.ByteOrder.LITTLE_ENDIAN);
        buffer.put("data".getBytes());
        buffer.putInt(0);
        return buffer;
    }

    public static ByteBuffer getWaveHeader(AudioFormat format) {
        ByteBuffer buffer = ByteBuffer.allocate(44);
        buffer.order(java.nio.ByteOrder.LITTLE_ENDIAN);
        buffer.put("RIFF".getBytes());
        buffer.putInt(0); // file size
        buffer.put("WAVE".getBytes());
        buffer.put("fmt ".getBytes());
        buffer.putInt(16); // format chunk size
        buffer.putShort((short) 1); // PCM
        buffer.putShort((short) format.getChannels());
        buffer.putInt((int) (format.getSampleRate() * format.getSampleSizeInBits() / 8 * format.getChannels()));
        buffer.putInt((int) (format.getSampleRate() * format.getSampleSizeInBits() / 8));
        buffer.putShort((short) format.getSampleSizeInBits());
        buffer.putShort((short) 1); // no compression
        buffer.put("data".getBytes());
        buffer.putInt(0); // data chunk size
        return buffer;
    }

    public static ByteBuffer getFormatChunk(AudioFormat format) {
        ByteBuffer buffer = ByteBuffer.allocate(16);
        buffer.order(java.nio.ByteOrder.LITTLE_ENDIAN);
        buffer.putShort((short) 1); // PCM
        buffer.putShort((short) format.getChannels());
        buffer.putInt((int) (format.getSampleRate() * format.getSampleSizeInBits() / 8 * format.getChannels()));
        buffer.putInt((int) (format.getSampleRate() * format.getSampleSizeInBits() / 8));
        buffer.putShort((short) format.getSampleSizeInBits());
        buffer.putShort((short) 1); // no compression
        return buffer;
    }

    private void writeWaveHeader() {
        try {
            mFileChannel.write(getWaveHeader(mAudioFormat));
        } catch (IOException e) {
            throw new RuntimeException("Failed to write wave header", e);
        }
    }
}

