import redis.clients.jedis.HostAndPort;
import redis.clients.jedis.Jedis;
import redis.clients.jedis.JedisClientConfig;
import redis.clients.jedis.JedisSentinelPool;
import redis.clients.jedis.exceptions.JedisConnectionException;

import java.util.Set;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

public class SentineledConnectionProvider {
    private HostAndPort currentMaster;
    private JedisSentinelPool pool;
    private String masterName;
    private JedisClientConfig masterClientConfig;
    private JedisClientConfig sentinelClientConfig;
    private ExecutorService listenerExecutor;

    public SentineledConnectionProvider(String masterName, JedisClientConfig masterClientConfig, Set<HostAndPort> sentinels, JedisClientConfig sentinelClientConfig) {
        this.masterName = masterName;
        this.masterClientConfig = masterClientConfig;
        this.sentinelClientConfig = sentinelClientConfig;
        initMaster(initSentinels(sentinels));
        listenerExecutor = Executors.newSingleThreadExecutor();
        listenerExecutor.execute(new SentinelListener(currentMaster));
    }

    public Jedis getConnection() {
        return pool.getResource();
    }

    private void initMaster(HostAndPort master) {
        this.currentMaster = master;
        pool = new JedisSentinelPool(masterName, sentinels, masterClientConfig, sentinelClientConfig);
    }

    private HostAndPort initSentinels(Set<HostAndPort> sentinels) {
        JedisSentinelPool sentinelPool = new JedisSentinelPool(masterName, sentinels, sentinelClientConfig);
        try (Jedis jedis = sentinelPool.getResource()) {
            return HostAndPort.fromSocketAddress(jedis.sentinelGetMasterAddrByName(masterName));
        } catch (JedisConnectionException e) {
            throw new RuntimeException("Failed to connect to Sentinel nodes", e);
        }
    }

    private class SentinelListener extends Thread {
        private HostAndPort node;

        public SentinelListener(HostAndPort node) {
            this.node = node;
        }

        @Override
        public void run() {
            while (!isInterrupted()) {
                try {
                    Thread.sleep(5000);
                    HostAndPort newMaster = initSentinels(sentinels);
                    if (!newMaster.equals(currentMaster)) {
                        initMaster(newMaster);
                    }
                } catch (InterruptedException e) {
                    interrupt();
                } catch (Exception e) {
                    // Handle error
                }
            }
        }
    }

    public void shutdown() {
        listenerExecutor.shutdownNow();
        if (pool != null) {
            pool.destroy();
        }
    }
}

