import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;

public class ArrayLongLongMapMmap {
    private final Path path;
    private FileChannel writeChannel;
    private final MappedByteBuffer[] segmentsArray;
    private FileChannel readChannel;
    private static final int SEGMENT_SIZE = 1 << 27;

    public ArrayLongLongMapMmap(Path path, boolean madvise) throws IOException {
        this.path = path;
        this.segmentsArray = new MappedByteBuffer[0];
        if (Files.exists(path)) {
            this.readChannel = FileChannel.open(path, StandardOpenOption.READ);
        } else {
            this.writeChannel = FileChannel.open(path, StandardOpenOption.CREATE_NEW, StandardOpenOption.WRITE);
        }
    }

    public void init() throws IOException {
        if (writeChannel != null) {
            writeChannel.close();
            writeChannel = null;
        }
        if (readChannel == null) {
            readChannel = FileChannel.open(path, StandardOpenOption.READ);
        }
        long fileSize = readChannel.size();
        int numSegments = (int) (fileSize + SEGMENT_SIZE - 1) / SEGMENT_SIZE;
        segmentsArray = new MappedByteBuffer[numSegments];
        for (int i = 0; i < numSegments; i++) {
            long segmentOffset = (long) i * SEGMENT_SIZE;
            segmentsArray[i] = readChannel.map(FileChannel.MapMode.READ_ONLY, segmentOffset, SEGMENT_SIZE);
        }
    }

    public long get(long key) {
        int index = (int) (key >>> 32);
        int offset = (int) (key & 0xFFFFFFFF);
        if (index >= segmentsArray.length) {
            return -1;
        }
        MappedByteBuffer buffer = segmentsArray[index];
        if (buffer.remaining() < offset + 8) {
            return -1;
        }
        buffer.position(offset);
        return buffer.getLong();
    }

    public void close() throws IOException {
        for (MappedByteBuffer buffer : segmentsArray) {
            if (buffer != null) {
                buffer.force();
                buffer.clear();
            }
        }
        if (readChannel != null) {
            readChannel.close();
        }
        if (Files.exists(path)) {
            Files.delete(path);
        }
    }
}

