import java.io.File;
import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.util.concurrent.atomic.AtomicBoolean;
import org.agrona.DirectBuffer;
import org.agrona.UnsafeBuffer;
import org.agrona.concurrent.EpochClock;
import org.agrona.concurrent.IdleStrategy;

public class MarkFile implements AutoCloseable {
    private final File markFile;
    private final MappedByteBuffer mappedBuffer;
    private final UnsafeBuffer buffer;
    private final AtomicBoolean isClosed = new AtomicBoolean(false);

    public MarkFile(File markFile, boolean create, int versionFieldOffset, int timestampFieldOffset, int length, long initialTimestamp, EpochClock clock, IntConsumer heartbeatHandler, Consumer<String> errorHandler) throws IOException {
        this.markFile = markFile;
        this.mappedBuffer = mapNewOrExistingMarkFile(markFile, create, versionFieldOffset, timestampFieldOffset, length, initialTimestamp, clock, heartbeatHandler, errorHandler);
        this.buffer = new UnsafeBuffer(mappedBuffer);
    }

    @Override
    public void close() {
        if (isClosed.compareAndSet(false, true)) {
            try {
                if (mappedBuffer != null) {
                    mappedBuffer.force();
                    mappedBuffer.clear();
                }
            } finally {
                // Close the file channel if needed
            }
        }
    }

    private static MappedByteBuffer mapNewOrExistingMarkFile(File markFile, boolean create, int versionFieldOffset, int timestampFieldOffset, int length, long initialTimestamp, EpochClock clock, IntConsumer heartbeatHandler, Consumer<String> errorHandler) throws IOException {
        FileChannel fileChannel = null;
        MappedByteBuffer mappedBuffer = null;
        try {
            fileChannel = FileChannel.open(markFile.toPath(), StandardOpenOption.READ, StandardOpenOption.WRITE, StandardOpenOption.CREATE_NEW);
        } catch (IOException e) {
            if (!create) {
                throw e;
            }
            try {
                fileChannel = FileChannel.open(markFile.toPath(), StandardOpenOption.READ, StandardOpenOption.WRITE);
            } catch (IOException ex) {
                errorHandler.accept("Failed to open existing mark file: " + ex.getMessage());
                throw ex;
            }
        }

        try {
            mappedBuffer = fileChannel.map(FileChannel.MapMode.READ_WRITE, 0, length);
            if (mappedBuffer.capacity() < length) {
                errorHandler.accept("Mapped buffer size is smaller than requested: " + mappedBuffer.capacity() + " < " + length);
                throw new IOException("Mapped buffer size is smaller than requested");
            }

            int version = buffer.getInt(versionFieldOffset);
            if (version != 1) {
                errorHandler.accept("Unsupported version: " + version);
                throw new IOException("Unsupported version");
            }

            long lastHeartbeat = buffer.getLong(timestampFieldOffset);
            if (lastHeartbeat > 0 && clock.currentTimeMillis() - lastHeartbeat > 5000) {
                heartbeatHandler.accept(0);
            }

            return mappedBuffer;
        } catch (Exception e) {
            errorHandler.accept("Error mapping mark file: " + e.getMessage());
            throw e;
        } finally {
            if (fileChannel != null) {
                try {
                    fileChannel.close();
                } catch (IOException e) {
                    errorHandler.accept("Error closing file channel: " + e.getMessage());
                }
            }
        }
    }

    private static void ensureMarkFileLink(File markFile, File linkDir, String linkName) throws IOException {
        Path linkPath = linkDir.toPath().resolve(linkName);
        if (Files.exists(linkPath)) {
            Files.delete(linkPath);
        }
        Files.createSymbolicLink(linkPath, markFile.toPath());
    }
}

