import org.apache.commons.cli.*;
import org.apache.rocketmq.client.consumer.DefaultMQPushConsumer;
import org.apache.rocketmq.client.consumer.listener.*;
import org.apache.rocketmq.common.message.MessageExt;

public class TimerConsumer {
    private String topic;
    private DefaultMQPushConsumer consumer;

    public TimerConsumer(String[] args) throws Exception {
        Options options = new Options();
        buildCommandlineOptions(options);
        CommandLineParser parser = new DefaultParser();
        CommandLine cmd = parser.parse(options, args);

        String nameServer = cmd.getOptionValue("n", "localhost:9876");
        topic = cmd.getOptionValue("t", "TestTopic");

        consumer = new DefaultMQPushConsumer("TimerConsumerGroup");
        consumer.setNamesrvAddr(nameServer);
    }

    public void start() throws MQClientException {
        consumer.subscribe(topic, "*");
        consumer.registerMessageListener((MessageListenerConcurrently) (msgs, context) -> {
            for (MessageExt msg : msgs) {
                long delay = System.currentTimeMillis() - msg.getBornTimestamp();
                System.out.println("Received message: " + new String(msg.getBody()) +
                        ", Delay: " + delay + " ms");
            }
            return ConsumeConcurrentlyStatus.CONSUME_SUCCESS;
        });
        consumer.start();
    }

    public Options buildCommandlineOptions(Options options) {
        Option nameServerOpt = new Option("n", "namesrv", true, "Name server address");
        nameServerOpt.setRequired(false);
        options.addOption(nameServerOpt);

        Option topicOpt = new Option("t", "topic", true, "Topic to subscribe");
        topicOpt.setRequired(false);
        options.addOption(topicOpt);

        return options;
    }

    public static void main(String[] args) {
        try {
            TimerConsumer consumer = new TimerConsumer(args);
            consumer.start();
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}

