import java.net.http.HttpResponse.BodyHandlers;
import java.time.Duration;
import java.util.concurrent.CompletableFuture;

public class BroadcastifyHttpClient {
    private final HttpClient mHttpClient;

    public BroadcastifyHttpClient() {
        mHttpClient = HttpClient.newBuilder()
                .version(HttpClient.Version.HTTP_2)
                .connectTimeout(Duration.ofSeconds(20))
                .followRedirects(HttpClient.Redirect.NORMAL)
                .build();
    }

    public String testConnection(BroadcastifyCallConfiguration configuration) {
        try {
            HttpRequest request = HttpRequest.newBuilder()
                    .uri(URI.create("https://api.broadcastify.com/v1/test"))
                    .header("Content-Type", "multipart/form-data")
                    .POST(HttpRequest.BodyPublishers.ofFormData(
                            "apiKey", configuration.getApiKey(),
                            "systemId", configuration.getSystemId(),
                            "test", "true"
                    ))
                    .build();

            HttpResponse<String> response = mHttpClient.send(request, BodyHandlers.ofString());
            return response.body();
        } catch (Exception e) {
            return "Error: " + e.getMessage();
        }
    }

    public void sendAsyncRequest(HttpRequest request) {
        CompletableFuture<HttpResponse<String>> future = mHttpClient.sendAsync(request, BodyHandlers.ofString());

        future.thenAccept(response -> {
            if (response.statusCode() == 200) {
                System.out.println("Success: " + response.body());
            } else {
                System.err.println("Error: " + response.statusCode() + " - " + response.body());
            }
        }).exceptionally(ex -> {
            System.err.println("Request failed: " + ex.getMessage());
            return null;
        });
    }
}

