import java.io.*;
import java.net.*;
import javax.security.auth.Subject;
import javax.security.auth.login.LoginContext;
import javax.security.auth.login.LoginException;
import org.ietf.jgss.*;

public class GSSClient {
    private Socket s;

    public static void main(String[] args) {
        GSSClient client = new GSSClient();
        if (args.length < 2) {
            client.usage();
            client.exit(1);
        }
        String serverName = args[0];
        String mechOidStr = args[1];
        Oid mechOid;
        try {
            mechOid = new Oid(mechOidStr);
        } catch (GSSException e) {
            client.print("Invalid mechanism OID: " + mechOidStr);
            client.exit(1);
            return;
        }
        try {
            client.s = new Socket(serverName, 12345);
            GSSContext ctxt = client.createCtxt(serverName, mechOid);
            if (ctxt.isEstablished()) {
                client.print("Security context established.");
                client.sendMsgToPeer(ctxt, "Hello from client");
                BufferedReader in = new BufferedReader(new InputStreamReader(client.s.getInputStream()));
                String response = in.readLine();
                client.verifyMsgFromPeer(ctxt, response);
                client.print("Received and verified message: " + response);
            } else {
                client.print("Failed to establish security context.");
            }
        } catch (IOException | GSSException e) {
            client.print("Error: " + e.getMessage());
            client.exit(1);
        } finally {
            client.exit(0);
        }
    }

    private GSSContext createCtxt(String serverName, Oid mechOid) throws GSSException, IOException {
        GSSManager manager = GSSManager.getInstance();
        GSSNameServer serverNameObj = manager.createName(serverName, GSSName.NT_HOSTBASED_SERVICE);
        GSSCredential clientCred = manager.createCredential(null, GSSCredential.INDEFINITE_LIFETIME, new Oid[]{mechOid}, GSSCredential.DELEGATE);
        GSSContext ctxt = manager.createContext(serverNameObj, mechOid, clientCred, GSSContext.DEFAULT_LIFETIME);
        byte[] token = new byte[0];
        OutputStream out = s.getOutputStream();
        while (!ctxt.isEstablished()) {
            token = ctxt.initSecContext(token, 0, token.length);
            if (token.length > 0) {
                out.write(token);
                out.flush();
            }
        }
        return ctxt;
    }

    private void sendMsgToPeer(GSSContext aCtxt, String msg) throws IOException, GSSException {
        byte[] msgBytes = msg.getBytes();
        byte[] token = aCtxt.wrap(msgBytes, 0, msgBytes.length, GSSContext.DEFAULT_QOP);
        OutputStream out = s.getOutputStream();
        out.write(token);
        out.flush();
    }

    private void verifyMsgFromPeer(GSSContext aCtxt, String msg) throws IOException, GSSException {
        byte[] msgBytes = msg.getBytes();
        byte[] token = new byte[msgBytes.length];
        int len = 0;
        InputStream in = s.getInputStream();
        while (len < token.length) {
            int read = in.read(token, len, token.length - len);
            if (read == -1) {
                throw new IOException("Unexpected end of stream");
            }
            len += read;
        }
        aCtxt.unwrap(token, 0, token.length);
    }

    private void usage() {
        print("Usage: java GSSClient <server_name> <mechanism_OID>");
    }

    private void print(String msg) {
        System.out.println(msg);
    }

    private void exit(int status) {
        try {
            if (s != null && !s.isClosed()) {
                s.close();
            }
        } catch (IOException e) {
            // Ignore
        }
        System.exit(status);
    }
}

