    private Reader reader;
    private Writer writer;
    private static final boolean USE_ZIP = true;

    public DeviceConnection() throws IOException {
        socket = new Socket("localhost", 4242);
        reader = new Reader(socket.getInputStream());
        writer = new Writer(socket.getOutputStream());
    }

    public void close() throws IOException {
        socket.close();
    }

    public ArrayList<String> listLayoutFiles() throws IOException {
        ArrayList<String> files = new ArrayList<>();
        writer.println("LIST_LAYOUT_FILES");
        String line;
        while ((line = reader.nextLine()) != null && !line.isEmpty()) {
            files.add(line);
        }
        complete();
        return files;
    }

    public BufferedImage takePicture() throws IOException {
        writer.println("TAKE_PICTURE");
        String response = reader.nextLine();
        if (!response.equals("OK")) {
            throw new IOException("Server did not confirm picture request: " + response);
        }

        InputStream inputStream = socket.getInputStream();
        BufferedImage image = null;
        if (USE_ZIP) {
            ZipInputStream zipInputStream = new ZipInputStream(inputStream);
            ZipEntry entry = zipInputStream.getNextEntry();
            if (entry != null) {
                image = ImageIO.read(zipInputStream);
                zipInputStream.closeEntry();
            }
            zipInputStream.close();
        } else {
            image = ImageIO.read(inputStream);
        }
        complete();
        return image;
    }

    public void complete() throws IOException {
        String line = reader.nextLine();
        if (!line.equals("COMPLETE")) {
            throw new IOException("Server did not complete operation: " + line);
        }
    }

    private class Reader {
        private BufferedReader bufferedReader;

        Reader(InputStream inputStream) throws IOException {
            bufferedReader = new BufferedReader(new InputStreamReader(inputStream));
        }

        public String nextLine() throws IOException {
            return bufferedReader.readLine();
        }
    }

    private class Writer {
        private BufferedWriter bufferedWriter;

        Writer(OutputStream outputStream) throws IOException {
            bufferedWriter = new BufferedWriter(new OutputStreamWriter(outputStream));
        }

        public void println(String line) throws IOException {
            bufferedWriter.write(line);
            bufferedWriter.newLine();
            bufferedWriter.flush();
        }
    }
}

