public class FileSender implements Runnable {
    private String mServerIpAddress;
    private int mPort;
    private FileInfo mFileInfo;
    private Socket mSocket;
    private OutputStream mOutputStream;
    private OnSendListener mOnSendListener;

    public FileSender(String serverIpAddress, int port, FileInfo fileInfo) {
        this.mServerIpAddress = serverIpAddress;
        this.mPort = port;
        this.mFileInfo = fileInfo;
    }

    public void setOnSendListener(OnSendListener listener) {
        this.mOnSendListener = listener;
    }

    @Override
    public void run() {
        try {
            init();
            if (mOnSendListener != null) {
                mOnSendListener.onStart();
            }
            parseHeader();
            parseBody();
            finish();
            if (mOnSendListener != null) {
                mOnSendListener.onSuccess(mFileInfo);
            }
        } catch (IOException e) {
            if (mOnSendListener != null) {
                mOnSendListener.onFailure(e, mFileInfo);
            }
        }
    }

    private void init() throws IOException {
        mSocket = new Socket(mServerIpAddress, mPort);
        mOutputStream = new BufferedOutputStream(mSocket.getOutputStream());
    }

    private void parseHeader() throws IOException {
        String header = mFileInfo.getFileName() + "," + mFileInfo.getFileSize() + "," + mFileInfo.getMimeType();
        byte[] headerBytes = header.getBytes();
        mOutputStream.write(headerBytes);
        mOutputStream.flush();
    }

    private void parseBody() throws IOException {
        FileInputStream fileInputStream = new FileInputStream(mFileInfo.getFile());
        byte[] buffer = new byte[4096];
        int bytesRead;
        long totalBytesRead = 0;
        while ((bytesRead = fileInputStream.read(buffer)) != -1) {
            mOutputStream.write(buffer, 0, bytesRead);
            mOutputStream.flush();
            totalBytesRead += bytesRead;
            if (mOnSendListener != null) {
                mOnSendListener.onProgress(totalBytesRead, mFileInfo.getFileSize());
            }
        }
        fileInputStream.close();
    }

    private void finish() throws IOException {
        mOutputStream.close();
        mSocket.close();
    }

    public interface OnSendListener {
        void onStart();
        void onProgress(long bytesSent, long totalBytes);
        void onSuccess(FileInfo fileInfo);
        void onFailure(Throwable throwable, FileInfo fileInfo);
    }

    public static class FileInfo {
        private File file;
        private String fileName;
        private long fileSize;
        private String mimeType;

        public FileInfo(File file) {
            this.file = file;
            this.fileName = file.getName();
