import java.io.*;
import java.net.*;

public class HttpClient {
    private static final String HOST = "127.0.0.1";
    private static final int PORT = 8080;

    public void testClient(String url) throws IOException {
        Socket socket = new Socket(HOST, PORT);
        OutputStream out = socket.getOutputStream();
        InputStream in = socket.getInputStream();

        // Send HTTP upgrade request
        String upgradeRequest = "GET " + url + " HTTP/1.1\r\n" +
                                "Host: " + HOST + ":" + PORT + "\r\n" +
                                "Connection: Upgrade\r\n" +
                                "Upgrade: websocket\r\n" +
                                "Sec-WebSocket-Key: dGhlIHNhbXBsZSBub25jZQ==\r\n" +
                                "Sec-WebSocket-Version: 13\r\n\r\n";
        out.write(upgradeRequest.getBytes());
        out.flush();

        // Read server response
        byte[] responseBytes = readBytes(in, 1024);
        String response = new String(responseBytes);
        if (!response.contains("HTTP/1.1 101 Switching Protocols")) {
            throw new IOException("Upgrade failed");
        }

        // Send echo message
        String echoMessage = "Hello, Server!\r\n";
        out.write(echoMessage.getBytes());
        out.flush();

        // Read echo response
        byte[] echoResponseBytes = readBytes(in, 1024);
        String echoResponse = new String(echoResponseBytes);
        if (!echoResponse.contains("Echo: Hello, Server!")) {
            throw new IOException("Echo response mismatch");
        }

        socket.close();
    }

    private byte[] readBytes(InputStream in, int length) throws IOException {
        byte[] buffer = new byte[length];
        int bytesRead = in.read(buffer);
        if (bytesRead < 0) {
            throw new IOException("No data received");
        }
        return buffer;
    }
}

