            }
            catch(Exception e) {
                e.printStackTrace();
            }
            for(;;) {
                total_bytes_sent.add(NioServerPerfTest.SIZE);
                if(total_bytes_sent.sum() > NioServerPerfTest.BYTES_TO_SEND)
                    break;
                buf.rewind();
                try {
                    ch.write(buf);
                    total_msgs.increment();
                }
                catch(IOException e) {
                    e.printStackTrace();
                }
            }
            Util.close(ch);
        }
    }
