import java.io.IOException;
import java.net.InetAddress;
import java.nio.ByteBuffer;
import java.nio.channels.SelectionKey;
import java.nio.channels.Selector;
import java.nio.channels.SocketChannel;
import java.util.concurrent.CountDownLatch;
import java.util.concurrent.atomic.LongAdder;

public class NioClientTest {
    private boolean running;
    private final LongAdder total_bytes_sent = new LongAdder();
    private final LongAdder total_msgs = new LongAdder();
    private Sender[] senders;

    public ByteBuffer create(int size, boolean direct) {
        return direct ? ByteBuffer.allocateDirect(size) : ByteBuffer.allocate(size);
    }

    public void start(InetAddress host, boolean direct, int num_threads) {
        running = true;
        senders = new Sender[num_threads];
        for (int i = 0; i < num_threads; i++) {
            senders[i] = new Sender(host, direct, new CountDownLatch(1));
            senders[i].start();
        }
        handleInput();
    }

    public void sendMessages(InetAddress host, boolean direct, int num_threads) {
        senders = new Sender[num_threads];
        for (int i = 0; i < num_threads; i++) {
            senders[i] = new Sender(host, direct, new CountDownLatch(1));
            senders[i].start();
        }
    }

    private void handleInput() {
        java.util.Scanner scanner = new java.util.Scanner(System.in);
        while (running) {
            String input = scanner.nextLine();
            if (input.equalsIgnoreCase("exit")) {
                running = false;
                for (Sender sender : senders) {
                    sender.interrupt();
                }
                System.out.println("Total bytes sent: " + total_bytes_sent.sum());
                System.out.println("Total messages sent: " + total_msgs.sum());
                break;
            }
        }
        scanner.close();
    }

    private class Sender extends Thread {
        private final SocketChannel ch;
        private final CountDownLatch latch;
        private final InetAddress host;
        private final boolean direct;
        private final ByteBuffer buf;

        public Sender(InetAddress host, boolean direct, CountDownLatch latch) {
            this.host = host;
            this.direct = direct;
            this.latch = latch;
            this.buf = create(1024, direct);
            try {
                this.ch = SocketChannel.open();
                ch.configureBlocking(false);
                ch.connect(host.getAddress());
            } catch (IOException e) {
                throw new RuntimeException("Failed to open channel", e);
            }
        }

        @Override
        public void run() {
            try {
                latch.await();
                Selector selector = Selector.open();
                ch.register(selector, SelectionKey.OP_CONNECT);
                while (running) {
                    selector.select();
                    for (SelectionKey key : selector.selectedKeys()) {
                        if (key.isConnectable()) {
                            if (ch.finishConnect()) {
                                ch.register(selector, SelectionKey.OP_WRITE);
                            }
                        } else if (key.isWritable()) {
                            synchronized (buf) {
                                buf.clear();
                                buf.put("Hello from NIO client".getBytes());
                                buf.flip();
                                ch.write(buf);
                                total_bytes_sent.add(buf.remaining());
                                total_msgs.increment();
                            }
                        }
                    }
                }
            } catch (Exception e) {
                e.printStackTrace();
            } finally {
                try {
                    ch.close();
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        }
    }
}

