import com.fasterxml.jackson.annotation.JsonIgnoreProperties;
import com.fasterxml.jackson.annotation.JsonProperty;
import com.fasterxml.jackson.databind.ObjectMapper;
import com.google.common.base.Preconditions;
import com.nimbusds.jose.JWEObject;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.io.IOException;
import java.io.UncheckedIOException;
import java.net.URI;
import java.net.http.HttpClient;
import java.net.http.HttpRequest;
import java.net.http.HttpResponse;
import java.nio.charset.StandardCharsets;
import java.text.ParseException;
import java.time.Duration;
import java.util.Objects;
import java.util.concurrent.CompletableFuture;
import java.util.concurrent.ExecutorService;

public class HubHttpClient {

	private static final Logger LOG = LoggerFactory.getLogger(HubHttpClient.class);
	private static final ObjectMapper JSON = new ObjectMapper().setDefaultLeniency(true);
	private static final Duration REQ_TIMEOUT = Duration.ofSeconds(10);

	private final HttpClient httpClient;
	private final String bearerToken;
	private final HubConfig hubConfig;
	private final String deviceId;
	private final String vaultId;

	public HubHttpClient(ExecutorService executor, HubConfig hubConfig, String deviceId, String bearerToken, String vaultId) {
		this.httpClient = HttpClient.newBuilder().version(HttpClient.Version.HTTP_1_1).executor(executor).build();
		this.bearerToken = Objects.requireNonNull(bearerToken);
		this.hubConfig = hubConfig;
		this.deviceId = deviceId;
		this.vaultId = vaultId;
	}

	public CompletableFuture<ReceivedKey> requestApiConfig() {
		var configUri = hubConfig.URIs.API.resolve("config");
		var request = HttpRequest.newBuilder(configUri)
				.GET()
				.timeout(REQ_TIMEOUT)
				.build();
		return httpClient.sendAsync(request, HttpResponse.BodyHandlers.ofString(StandardCharsets.US_ASCII))
				.thenCompose(this::handleApiConfigResponse)
				.exceptionally(this::retrievalFailed);
	}

	private CompletableFuture<ReceivedKey> handleApiConfigResponse(HttpResponse<String> response) {
		LOG.debug("GET {} -> Status Code {}", response.request().uri(), response.statusCode());
		Preconditions.checkState(response.statusCode() == 200, "Unexpected response " + response.statusCode());
		try {
			var config = JSON.reader().readValue(response.body(), ConfigDto.class);
			if (config.apiLevel >= 1) {
				return requestDeviceData();
			} else {
				return requestLegacyAccessToken();
			}
		} catch (IOException e) {
			throw new UncheckedIOException(e);
		}
	}

	private CompletableFuture<ReceivedKey> requestDeviceData() {
		var deviceUri = hubConfig.URIs.API.resolve("devices/" + deviceId);
		var request = HttpRequest.newBuilder(deviceUri)
				.header("Authorization", "Bearer " + bearerToken)
				.GET()
				.timeout(REQ_TIMEOUT)
				.build();
		return httpClient.sendAsync(request, HttpResponse.BodyHandlers.ofString(StandardCharsets.UTF_8))
				.thenCompose(this::handleDeviceDataResponse)
				.exceptionally(this::retrievalFailed);
	}

	private CompletableFuture<ReceivedKey> handleDeviceDataResponse(HttpResponse<String> response) {
		LOG.debug("GET {} -> Status Code {}", response.request().uri(), response.statusCode());
		try {
			switch (response.statusCode()) {
				case 200 -> {
					var device = JSON.reader().readValue(response.body(), DeviceDto.class);
					return requestVaultMasterkey(device.userPrivateKey);
				}
				default -> throw new IllegalStateException("Unexpected response " + response.statusCode());
			}
		} catch (IOException e) {
			throw new UncheckedIOException(e);
		}
	}

	private CompletableFuture<ReceivedKey> requestVaultMasterkey(String encryptedUserKey) {
		var vaultKeyUri = hubConfig.URIs.API.resolve("vaults/" + vaultId + "/access-token");
		var request = HttpRequest.newBuilder(vaultKeyUri)
				.header("Authorization", "Bearer " + bearerToken)
				.GET()
				.timeout(REQ_TIMEOUT)
				.build();
		return httpClient.sendAsync(request, HttpResponse.BodyHandlers.ofString(StandardCharsets.US_ASCII))
				.thenApply(response -> handleVaultMasterkeyResponse(encryptedUserKey, response))
				.exceptionally(this::retrievalFailed);
	}

	private ReceivedKey handleVaultMasterkeyResponse(String encryptedUserKey, HttpResponse<String> response) {
		LOG.debug("GET {} -> Status Code {}", response.request().uri(), response.statusCode());
		if (response.statusCode() == 200) {
			try {
				var vaultKeyJwe = JWEObject.parse(response.body());
				var userKeyJwe = JWEObject.parse(encryptedUserKey);
				return ReceivedKey.vaultKeyAndUserKey(vaultKeyJwe, userKeyJwe);
			} catch (ParseException e) {
				throw new RuntimeException(e);
			}
		} else {
			throw new IllegalStateException("Unexpected response " + response.statusCode());
		}
	}

	private CompletableFuture<ReceivedKey> requestLegacyAccessToken() {
		var legacyAccessTokenUri = hubConfig.URIs.API.resolve("vaults/" + vaultId + "/keys/" + deviceId);
		var request = HttpRequest.newBuilder(legacyAccessTokenUri)
				.header("Authorization", "Bearer " + bearerToken)
				.GET()
				.timeout(REQ_TIMEOUT)
				.build();
		return httpClient.sendAsync(request, HttpResponse.BodyHandlers.ofString(StandardCharsets.US_ASCII))
				.thenApply(this::handleLegacyAccessTokenResponse)
				.exceptionally(this::retrievalFailed);
	}

	private ReceivedKey handleLegacyAccessTokenResponse(HttpResponse<String> response) {
		try {
			if (response.statusCode() == 200) {
				var token = JWEObject.parse(response.body());
				return ReceivedKey.legacyDeviceKey(token);
			} else {
				throw new IOException("Unexpected response " + response.statusCode());
			}
		} catch (IOException | ParseException e) {
			throw new UncheckedIOException(e instanceof IOException ? (IOException) e : new IOException(e));
		}
	}

	private ReceivedKey retrievalFailed(Throwable cause) {
		throw new RuntimeException(cause);
	}

	@JsonIgnoreProperties(ignoreUnknown = true)
	private record DeviceDto(@JsonProperty(value = "userPrivateKey", required = true) String userPrivateKey) {}

	@JsonIgnoreProperties(ignoreUnknown = true)
	private record ConfigDto(@JsonProperty(value = "apiLevel") int apiLevel) {}
}

