    private ByteBuffer byteBufferRead;
    private ByteBuffer byteBufferBackup;
    private final DefaultMessageStore defaultMessageStore;

    public DefaultHAClient(DefaultMessageStore defaultMessageStore) {
        this.defaultMessageStore = defaultMessageStore;
        try {
            selector = Selector.open();
            byteBufferRead = ByteBuffer.allocate(READ_MAX_BUFFER_SIZE);
            byteBufferBackup = ByteBuffer.allocate(READ_MAX_BUFFER_SIZE);
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void updateHaMasterAddress(String newAddr) {
        masterHaAddress.set(newAddr);
    }

    public boolean reportSlaveMaxOffset(long maxOffset) {
        if (socketChannel == null || !socketChannel.isConnected()) {
            return false;
        }
        reportOffset.putLong(maxOffset);
        reportOffset.flip();
        try {
            socketChannel.write(reportOffset);
            lastWriteTimestamp = System.currentTimeMillis();
            reportOffset.clear();
            return true;
        } catch (IOException e) {
            e.printStackTrace();
            return false;
        }
    }

    private void reallocateByteBuffer() {
        if (byteBufferRead.capacity() < READ_MAX_BUFFER_SIZE) {
            byteBufferBackup = ByteBuffer.allocate(READ_MAX_BUFFER_SIZE);
            swapByteBuffer();
        }
    }

    private void swapByteBuffer() {
        ByteBuffer temp = byteBufferRead;
        byteBufferRead = byteBufferBackup;
        byteBufferBackup = temp;
        byteBufferRead.clear();
    }

    private boolean processReadEvent() {
        if (socketChannel == null || !socketChannel.isConnected()) {
            return false;
        }
        try {
            int bytesRead = socketChannel.read(byteBufferRead);
            if (bytesRead > 0) {
                lastReadTimestamp = System.currentTimeMillis();
                byteBufferRead.flip();
                while (byteBufferRead.hasRemaining()) {
                    defaultMessageStore.dispatchMessage(byteBufferRead.get());
                }
                byteBufferRead.compact();
                return true;
            } else if (bytesRead == 0) {
                return true;
            } else {
                return false;
            }
        } catch (IOException e) {
            e.printStackTrace();
            return false;
        }
    }

    private boolean connectMaster() {
        String address = masterHaAddress.get();
        if (address == null || address.isEmpty()) {
            return false;
        }
        try {
            socketChannel = SocketChannel.open();
            socketChannel.configureBlocking(false);
            socketChannel.connect(new InetSocketAddress(address));
            socketChannel.register(selector, SelectionKey.OP_CONNECT);
            return true;
        } catch (IOException e) {
            e.printStackTrace();
            return false;
        }
    }

    private void closeMaster() {
        if (socketChannel != null) {
            try {
                socketChannel.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
            socketChannel = null;
        }
        if (selector != null) {
            try {
                selector.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    @Override
    public void run() {
        while (!Thread.interrupted()) {
            try {
                selector.select();
                for (SelectionKey key : selector.selectedKeys()) {
                    if (key.isConnectable()) {
                        if (socketChannel.finishConnect()) {
                            socketChannel.register(selector, SelectionKey.OP_READ);
                        }
                    } else if (key.isReadable()) {
                        processReadEvent();
                    }
                }
                selector.selectedKeys().clear();
            } catch (IOException e) {
                e.printStackTrace();
                closeMaster();
                break;
            }
        }
    }

    public void shutdown() {
        closeMaster();
    }
}

