import java.io.IOException;
import java.net.InetAddress;
import java.nio.ByteBuffer;
import java.nio.channels.SelectionKey;
import java.nio.channels.Selector;
import java.nio.channels.ServerSocketChannel;
import java.nio.channels.SocketChannel;
import java.util.Iterator;
import java.util.Set;

public class NioTransport {
    private ServerSocketChannel srv_channel;
    private SocketChannel client_channel;
    private Receiver receiver_thread;
    private RtReceiver receiver;
    private InetAddress host;
    private int port = 7800;
    private boolean server = false;
    private boolean direct_buffers = false;
    private Log log;

    public void options(String... options) {
        for (String opt : options) {
            if (opt.startsWith("server")) {
                server = true;
            } else if (opt.startsWith("client")) {
                server = false;
            } else if (opt.startsWith("host=")) {
                try {
                    host = InetAddress.getByName(opt.substring(5));
                } catch (Exception e) {
                    log.error("Invalid host: " + opt.substring(5));
                }
            } else if (opt.startsWith("port=")) {
                try {
                    port = Integer.parseInt(opt.substring(5));
                } catch (NumberFormatException e) {
                    log.error("Invalid port: " + opt.substring(5));
                }
            } else if (opt.equals("direct")) {
                direct_buffers = true;
            }
        }
    }

    public void receiver(RtReceiver receiver) {
        this.receiver = receiver;
    }

    public void start(String... options) {
        options(options);
        if (server) {
            try {
                srv_channel = ServerSocketChannel.open();
                srv_channel.configureBlocking(false);
                srv_channel.socket().bind(new java.net.InetSocketAddress(host, port));
                Selector selector = Selector.open();
                srv_channel.register(selector, SelectionKey.OP_ACCEPT);
                receiver_thread = new Receiver(selector);
                receiver_thread.start();
            } catch (IOException e) {
                log.error("Failed to start server: " + e.getMessage());
            }
        } else {
            try {
                client_channel = SocketChannel.open();
                client_channel.configureBlocking(false);
                client_channel.connect(new java.net.InetSocketAddress(host, port));
                Selector selector = Selector.open();
                client_channel.register(selector, SelectionKey.OP_CONNECT);
                receiver_thread = new Receiver(selector);
                receiver_thread.start();
            } catch (IOException e) {
                log.error("Failed to start client: " + e.getMessage());
            }
        }
    }

    public void stop() {
        try {
            if (srv_channel != null) {
                srv_channel.close();
            }
            if (client_channel != null) {
                client_channel.close();
            }
            if (receiver_thread != null) {
                receiver_thread.interrupt();
            }
        } catch (IOException e) {
            log.error("Failed to stop transport: " + e.getMessage());
        }
    }

    public void send(Object dest, byte[] buf, int offset, int length) {
        if (client_channel != null && client_channel.isConnected()) {
            ByteBuffer buffer = direct_buffers ? ByteBuffer.allocateDirect(length) : ByteBuffer.allocate(length);
            buffer.put(buf, offset, length);
            buffer.flip();
            try {
                client_channel.write(buffer);
            } catch (IOException e) {
                log.error("Failed to send data: " + e.getMessage());
            }
        }
    }

    private class Receiver extends Thread {
        private Selector selector;

        public Receiver(Selector selector) {
            this.selector = selector;
        }

        @Override
        public void run() {
            while (!isInterrupted()) {
                try {
                    selector.select();
                    Set<SelectionKey> selectedKeys = selector.selectedKeys();
                    Iterator<SelectionKey> iter = selectedKeys.iterator();
                    while (iter.hasNext()) {
                        SelectionKey key = iter.next();
                        iter.remove();
                        if (!key.isValid()) {
                            continue;
                        }
                        if (key.isAcceptable()) {
                            ServerSocketChannel serverChannel = (ServerSocketChannel) key.channel();
                            SocketChannel clientChannel = serverChannel.accept();
                            if (clientChannel != null) {
                                clientChannel.configureBlocking(false);
                                clientChannel.register(selector, SelectionKey.OP_READ);
                            }
                        } else if (key.isConnectable()) {
                            SocketChannel channel = (SocketChannel) key.channel();
                            if (channel.finishConnect()) {
                                channel.register(selector, SelectionKey.OP_READ);
                            }
                        } else if (key.isReadable()) {
                            SocketChannel channel = (SocketChannel) key.channel();
                            ByteBuffer buffer = direct_buffers ? ByteBuffer.allocateDirect(1024) : ByteBuffer.allocate(1024);
                            int bytesRead;
                            try {
                                while ((bytesRead = channel.read(buffer)) > 0) {
                                    buffer.flip();
                                    byte[] data = new byte[buffer.remaining()];
                                    buffer.get(data);
                                    buffer.clear();
                                    receiver.onDataReceived(data);
                                }
                            } catch (IOException e) {
                                log.error("Error reading from channel: " + e.getMessage());
                            }
                        }
                    }
                } catch (Exception e) {
                    log.error("Receiver thread error: " + e.getMessage());
                }
            }
        }
    }

    public interface RtReceiver {
        void onDataReceived(byte[] data);
    }

    public interface Log {
        void error(String message);
    }
}

