import java.io.IOException;
import java.net.InetSocketAddress;
import java.nio.ByteBuffer;
import java.nio.channels.SelectionKey;
import java.nio.channels.Selector;
import java.nio.channels.SocketChannel;
import java.util.concurrent.BlockingQueue;
import java.util.concurrent.LinkedBlockingQueue;

public class ClientController {
    private final BlockingQueue<Packet> inner = new LinkedBlockingQueue<>();
    private final Object lock = new Object();
    private final InetSocketAddress remote;
    private final Thread thread;
    private SocketChannel channel;

    public ClientController(String host, int port) {
        this.remote = new InetSocketAddress(host, port);
        this.thread = new Thread(new ClientThread());
    }

    public void start() {
        if (thread.isAlive()) return;
        thread.start();
    }

    public boolean put(Packet packet) {
        return inner.offer(packet);
    }

    public void clear() {
        inner.clear();
    }

    private class ClientThread implements Runnable {
        private Selector selector;

        public void run() {
            try {
                channel = SocketChannel.open();
                channel.configureBlocking(false);
                channel.connect(remote);

                selector = Selector.open();
                channel.register(selector, SelectionKey.OP_CONNECT);

                while (true) {
                    selector.select();
                    for (SelectionKey key : selector.selectedKeys()) {
                        selector.selectedKeys().remove(key);
                        if (!key.isValid()) continue;

                        if (key.isConnectable()) {
                            if (channel.finishConnect()) {
                                channel.register(selector, SelectionKey.OP_WRITE);
                                write();
                            }
                        } else if (key.isWritable()) {
                            write();
                        }
                    }
                }
            } catch (IOException e) {
                e.printStackTrace();
            } finally {
                close();
            }
        }

        private void write() {
            synchronized (lock) {
                Packet packet;
                while ((packet = inner.poll()) != null) {
                    byte[] data = packet.getData();
                    int checksum = calculateChecksum(data);
                    ByteBuffer buffer = ByteBuffer.allocate(data.length + 4);
                    buffer.putInt(checksum);
                    buffer.put(data);
                    buffer.flip();

                    try {
                        channel.write(buffer);
                    } catch (IOException e) {
                        e.printStackTrace();
                    }
                }
            }
        }

        private int calculateChecksum(byte[] data) {
            int checksum = 0;
            for (byte b : data) {
                checksum += b & 0xFF;
            }
            return checksum;
        }

        private void close() {
            try {
                if (channel != null && channel.isOpen()) {
                    channel.close();
                }
                if (selector != null && selector.isOpen()) {
                    selector.close();
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }
}

class Packet {
    private final byte[] data;

    public Packet(byte[] data) {
        this.data = data;
    }

    public byte[] getData() {
        return data;
    }
}

