import java.util.concurrent.*;
import java.util.concurrent.atomic.AtomicInteger;

public class RTMPMinaTransport {
    private static final Logger log = LoggerFactory.getLogger(RTMPMinaTransport.class);
    private ThreadPoolExecutor executor;
    private IoAcceptor acceptor;
    private Set<String> addresses = new HashSet<>();
    private IoHandlerAdapter ioHandler;
    private int ioThreads = 2 * Runtime.getRuntime().availableProcessors();
    private ObjectName serviceManagerObjectName;
    private IoServiceStatistics stats;
    private boolean enableMinaLogFilter = true;
    private boolean enableMinaMonitor = true;
    private int minaPollInterval = 1000;
    private boolean tcpNoDelay = true;
    private boolean useHeapBuffers = true;
    private int sendBufferSize = 65536;
    private int receiveBufferSize = 65536;
    private int readerIdleTime = 2;
    private int trafficClass = 0x08 | 0x10;
    private int backlog = 32;
    private int thoughputCalcInterval = 1;
    private long executorKeepAliveTime = 60000;
    private boolean enableDefaultAcceptor = true;
    private int initialPoolSize = 0;
    private int maxPoolSize = Runtime.getRuntime().availableProcessors() + 1;
    private int maxProcessorPoolSize = 16;
    private boolean keepAlive = false;

    public void start() throws Exception {
        if (addresses.isEmpty()) {
            throw new IllegalStateException("No addresses configured to bind to.");
        }

        if (ioHandler == null) {
            throw new IllegalStateException("IO handler not set.");
        }

        if (enableDefaultAcceptor) {
            acceptor = new NioSocketAcceptor(ioThreads);
        } else {
            executor = createThreadPoolExecutor();
            acceptor = new NioSocketAcceptor(executor, ioThreads);
        }

        configureAcceptor();

        for (String address : addresses) {
            String[] parts = address.split(":");
            if (parts.length != 2) {
                throw new IllegalArgumentException("Invalid address format: " + address);
            }
            String host = parts[0];
            int port = Integer.parseInt(parts[1]);
            acceptor.bind(new InetSocketAddress(host, port));
            log.info("Bound to address: {}", address);
        }

        if (enableMinaMonitor) {
            registerJMX();
        }
    }

    private void configureAcceptor() {
        acceptor.getFilterChain().addLast("logger", new LoggingFilter());
        acceptor.getFilterChain().addLast("codec", new ProtocolCodecFilter(new RTMPCodecFactory()));
        acceptor.getSessionConfig().setTcpNoDelay(tcpNoDelay);
        acceptor.getSessionConfig().setUseHeapBuffers(useHeapBuffers);
        acceptor.getSessionConfig().setSendBufferSize(sendBufferSize);
        acceptor.getSessionConfig().setReceiveBufferSize(receiveBufferSize);
        acceptor.getSessionConfig().setTrafficClass(trafficClass);
        acceptor.getSessionConfig().setBacklog(backlog);
        acceptor.getSessionConfig().setReaderIdleTime(readerIdleTime);
        acceptor.getSessionConfig().setIdleTime(IdleStatus.BOTH_IDLE, readerIdleTime);
        acceptor.setHandler(ioHandler);
    }

    private ThreadPoolExecutor createThreadPoolExecutor() {
        return new ThreadPoolExecutor(
                initialPoolSize,
                maxPoolSize,
                executorKeepAliveTime,
                TimeUnit.MILLISECONDS,
                new LinkedBlockingQueue<Runnable>(),
                new ThreadPoolExecutor.CallerRunsPolicy()
        );
    }

    public void stop() {
        if (acceptor != null) {
            acceptor.unbindAll();
            acceptor.dispose();
            acceptor = null;
        }
        if (executor != null) {
            executor.shutdownNow();
            executor = null;
        }
        if (stats != null) {
            stats.stop();
            stats = null;
        }
    }

    public void setAddress(String address) {
        addresses.add(address);
    }

    public void setAddresses(List<String> addrs) {
        addresses.addAll(addrs);
    }

    public void setIoHandler(IoHandlerAdapter rtmpIOHandler) {
        this.ioHandler = rtmpIOHandler;
    }

