      log.info("Client connected from: " + socket.remoteAddress());
      connectionLocalAddress = socket.remoteAddress();

      client.connect(remoteAddress, result -> {
        if (result.succeeded()) {
          connectionRemoteAddress = result.result().remoteAddress();
          log.info("Connected to remote server at: " + connectionRemoteAddress);

          // Write the header to the remote connection
          result.result().write(header, writeResult -> {
            if (writeResult.succeeded()) {
              log.info("Header written to remote server");
              // Pipe data between client and server
              socket.pipeTo(result.result());
              result.result().pipeTo(socket);
            } else {
              log.error("Failed to write header to remote server", writeResult.cause());
            }
          });
        } else {
          log.error("Failed to connect to remote server", result.cause());
        }
      });
    });

    server.exceptionHandler(err -> {
      log.error("Server exception", err);
    });

    server.listen(ar -> {
      if (ar.succeeded()) {
        log.info("Proxy server started on " + DEFAULT_HOST + ":" + DEFAULT_PORT);
      } else {
        log.error("Failed to start proxy server", ar.cause());
        throw new Exception("Failed to start proxy server", ar.cause());
      }
    });

    return this;
  }

  public void stop() {
    if (server != null) {
      server.close();
    }
    if (client != null) {
      client.close();
    }
    log.info("Proxy server stopped");
  }

  public SocketAddress getConnectionRemoteAddress() {
    return connectionRemoteAddress;
  }

  public SocketAddress getConnectionLocalAddress() {
    return connectionLocalAddress;
  }
}

