        try {
            admin = createAdmin();
            DescribeTopicsResult result = admin.describeTopics(Arrays.asList(topicNames));
            Map<String, TopicDescription> topicDescriptions = new HashMap<>();
            for (String topicName : topicNames) {
                try {
                    TopicDescription description = result.all().get(operationTimeout, TimeUnit.SECONDS).get(topicName);
                    topicDescriptions.put(topicName, description);
                } catch (ExecutionException e) {
                    throw new KafkaException("Failed to describe topic: " + topicName, e);
                } catch (InterruptedException | TimeoutException e) {
                    Thread.currentThread().interrupt();
                    throw new KafkaException("Interrupted or timed out while describing topic: " + topicName, e);
                }
            }
            return topicDescriptions;
        } catch (KafkaException e) {
            throw e;
        } catch (Exception e) {
            throw new KafkaException("Unexpected error while describing topics", e);
        } finally {
            if (admin != null) {
                try {
                    admin.close();
                } catch (Exception e) {
                    // Log or handle the exception as needed
                }
            }
        }
    }

    protected Admin createAdmin() {
        return AdminClient.create(new HashMap<>(configs));
    }

