import org.java_websocket.client.WebSocketClient;
import org.java_websocket.handshake.ServerHandshake;
import javax.net.ssl.SSLParameters;
import java.net.URI;
import java.net.URISyntaxException;
import java.security.cert.CertificateException;
import java.security.cert.X509Certificate;
import javax.net.ssl.*;

public class SSLWebSocketClient extends WebSocketClient {
    private String endpointIdentificationAlgorithm;
    private boolean onSSLError = false;
    private boolean onOpen = false;

    public SSLWebSocketClient(String address, int port, String endpointIdentificationAlgorithm) throws URISyntaxException {
        super(new URI("wss://" + address + ":" + port));
        this.endpointIdentificationAlgorithm = endpointIdentificationAlgorithm;
        // Set custom SSL context
        setSSLContext(createSSLContext());
    }

    private SSLContext createSSLContext() {
        try {
            SSLContext sslContext = SSLContext.getInstance("TLS");
            sslContext.init(null, new TrustManager[]{new X509TrustManager() {
                public void checkClientTrusted(X509Certificate[] chain, String authType) throws CertificateException {}
                public void checkServerTrusted(X509Certificate[] chain, String authType) throws CertificateException {}
                public X509Certificate[] getAcceptedIssuers() { return new X509Certificate[0]; }
            }}, new SecureRandom());
            return sslContext;
        } catch (Exception ex) {
            ex.printStackTrace();
            return null;
        }
    }

    @Override
    public void onOpen(ServerHandshake handshakedata) {
        onOpen = true;
        System.out.println("WebSocket connection opened.");
    }

    @Override
    public void onMessage(String message) {
        System.out.println("Received message: " + message);
    }

    @Override
    public void onClose(int code, String reason, boolean remote) {
        System.out.println("WebSocket closed with code: " + code + ", reason: " + reason + ", remote: " + remote);
    }

    @Override
    public void onError(Exception ex) {
        if (ex instanceof SSLException) {
            onSSLError = true;
            System.err.println("SSL Error occurred: " + ex.getMessage());
        } else {
            System.err.println("Error occurred: " + ex.getMessage());
        }
    }

    @Override
