import org.java_websocket.WebSocket;
import org.java_websocket.handshake.ClientHandshake;
import org.java_websocket.server.WebSocketServer;
import java.io.IOException;
import java.nio.channels.ServerSocketChannel;
import java.util.concurrent.atomic.AtomicInteger;

public class SocketActivation extends WebSocketServer {
    private final AtomicInteger clients = new AtomicInteger(0);

    public SocketActivation(ServerSocketChannel chan) {
        super(chan.socket(), null);
    }

    @Override
    public void onOpen(WebSocket conn, ClientHandshake handshake) {
        clients.incrementAndGet();
        conn.send("Welcome to the WebSocket server!");
        broadcast("Client connected: " + handshake.getResourceDescriptor());
    }

    @Override
    public void onClose(WebSocket conn, int code, String reason, boolean remote) {
        clients.decrementAndGet();
        broadcast("Client disconnected: " + reason);
        if (clients.get() == 0) {
            try {
                stop();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    @Override
    public void onMessage(WebSocket conn, String message) {
        broadcast("Received text: " + message);
        System.out.println("Text message received: " + message);
    }

    @Override
    public void onMessage(WebSocket conn, ByteBuffer message) {
        broadcast("Received binary data");
        System.out.println("Binary message received");
    }

    @Override
    public void onError(WebSocket conn, Exception ex) {
        ex.printStackTrace();
    }

    @Override
    public void onStart() {
        System.out.println("WebSocket server started on port " + getPort());
    }

    private void broadcast(String message) {
        for (WebSocket client : getConnections()) {
            client.send(message);
        }
    }
}

