    @Override
    public void onMessage(WebSocket conn, String message) {
        Integer clientId = clientIds.get(conn);
        System.out.println("Received text message from client ID: " + clientId + ": " + message);
    }

    @Override
    public void onMessage(WebSocket conn, ByteBuffer message) {
        Integer clientId = clientIds.get(conn);
        System.out.println("Received binary message from client ID: " + clientId);
    }

    @Override
    public void onError(WebSocket conn, Exception ex) {
        System.err.println("Error occurred on connection: " + conn);
