        if (accessToken == null || accessToken.isEmpty()) {
            logger.warning("Connection attempt without access token");
            try {
                session.close(new CloseReason(CloseReason.CloseCodes.UNAUTHORIZED, "Missing access token"));
            } catch (Exception e) {
                logger.severe("Failed to close unauthorized session: " + e.getMessage());
            }
            return;
        }
        logger.info("New connection established: " + session.getId());
    }

    @OnMessage
    public void onMessage(Session session, String message) {
        try {
            MessageHandler handler = findMessageHandler(message);
            if (handler != null) {
                Message msg = handler.parseMessage(message);
                handler.handleMessage(session, msg);
            } else {
                logger.warning("No handler found for message type: " + message);
            }
        } catch (Exception e) {
            logger.severe("Error processing message: " + e.getMessage());
            onError(session, e);
        }
    }

    @OnClose
    public void onClose(Session session, CloseReason closeReason) {
        logger.info("Connection closed: " + session.getId() + ", Reason: " + closeReason.getReasonPhrase());
    }

    @OnError
    public void onError(Session session, Throwable throwable) {
        logger.severe("WebSocket error for session " + session.getId() + ": " + throwable.getMessage());
        throwable.printStackTrace();
    }

    private MessageHandler findMessageHandler(String message) {
        // Implementation to determine the correct handler based on message content
        return null;
    }

    protected Class<? extends Message> getMessageClass(MessageHandler handler) {
        return handler.getMessageClass();
    }
}

