import java.util.List;
import java.util.concurrent.atomic.AtomicBoolean;
import java.util.concurrent.locks.ReentrantReadWriteLock;
import java.util.function.Function;
import java.util.concurrent.TimeUnit;

public class DiscoveryClient {
    private final AtomicBoolean requireResetStub = new AtomicBoolean(false);
    private final ManagedChannel channel;
    private final DiscoveryServiceBlockingStub registerStub;
    private final DiscoveryServiceBlockingStub blockingStub;
    private final ReentrantReadWriteLock readWriteLock = new ReentrantReadWriteLock();
    private final List<String> pdAddresses;

    public DiscoveryClient(String centerAddress) {
        this.channel = ManagedChannelBuilder.forAddress(centerAddress, 50051)
                .usePlaintext()
                .build();
        this.registerStub = DiscoveryServiceGrpc.newBlockingStub(channel);
        this.blockingStub = DiscoveryServiceGrpc.newBlockingStub(channel);
        this.pdAddresses = List.of(centerAddress);
    }

    public NodeInfos getNodeInfos(Query query) {
        return tryWithTimes((stub) -> stub.getNodeInfos(query), query);
    }

    private <V, R> R tryWithTimes(Function<DiscoveryServiceBlockingStub, R> function, V v) {
        int retryCount = 3;
        while (retryCount-- > 0) {
            try {
                readWriteLock.readLock().lock();
                if (requireResetStub.get()) {
                    resetStub();
                }
                return function.apply(blockingStub);
            } catch (StatusRuntimeException e) {
                if (retryCount == 0) {
                    throw e;
                }
                try {
                    TimeUnit.SECONDS.sleep(1);
                } catch (InterruptedException ie) {
                    Thread.currentThread().interrupt();
                }
            } finally {
                readWriteLock.readLock().unlock();
            }
        }
        return null;
    }

    private void resetStub() {
        readWriteLock.writeLock().lock();
        try {
            if (channel != null && !channel.isShutdown()) {
                channel.shutdownNow();
            }
            requireResetStub.set(false);
            resetChannel(pdAddresses.get(0));
        } finally {
            readWriteLock.writeLock().unlock();
        }
    }

    private void resetChannel(String address) {
        readWriteLock.writeLock().lock();
        try {
            if (channel != null && !channel.isShutdown()) {
                channel.shutdownNow();
            }
            channel.shutdown();
            this.channel = ManagedChannelBuilder.forAddress(address, 50051)
                    .usePlaintext()
                    .build();
            this.registerStub = DiscoveryServiceGrpc.newBlockingStub(channel);
            this.blockingStub = DiscoveryServiceGrpc.newBlockingStub(channel);
        } finally {
            readWriteLock.writeLock().unlock();
        }
    }

    public void close() {
        if (channel != null && !channel.isShutdown()) {
            channel.shutdownNow();
        }
    }
}

