import io.grpc.*;
import io.grpc.stub.AbstractBlockingStub;
import io.grpc.stub.AbstractStub;
import io.grpc.stub.StreamObserver;
import java.util.*;
import java.util.concurrent.*;
import java.util.concurrent.atomic.AtomicReference;
import java.util.function.Predicate;

public abstract class AbstractClient {
    protected final ConcurrentHashMap<String, ManagedChannel> chs = new ConcurrentHashMap<>();
    protected final AbstractClientStubProxy stubProxy;
    protected final PDConfig config;
    protected ManagedChannel channel;
    protected final ConcurrentMap<String, AbstractBlockingStub> stubs = new ConcurrentHashMap<>();

    public AbstractClient(PDConfig config) {
        this.config = config;
        this.stubProxy = new AbstractClientStubProxy(this);
        resetStub();
    }

    public AbstractBlockingStub getBlockingStub() {
        return stubProxy.getBlockingStub();
    }

    public AbstractStub getStub() {
        return stubProxy.getStub();
    }

    protected abstract AbstractStub createStub();

    protected abstract AbstractBlockingStub createBlockingStub();

    public String resetStub() {
        String leaderHost = findLeaderHost();
        if (leaderHost != null) {
            channel = connect(leaderHost);
            stubs.clear();
            return leaderHost;
        }
        return null;
    }

    protected String findLeaderHost() {
        // Implementation to find the current leader host
        return "localhost:2379";
    }

    protected ManagedChannel connect(String host) {
        if (chs.containsKey(host)) {
            return chs.get(host);
        }
        ManagedChannel newChannel = ManagedChannelBuilder.forAddress(host, 2379)
                .usePlaintext()
                .build();
        chs.put(host, newChannel);
        return newChannel;
    }

    public <ReqT, RespT> RespT blockingUnaryCall(MethodDescriptor<ReqT, RespT> method, ReqT req) {
        return blockingUnaryCall(method, req, config.getRetryCount());
    }

    public <ReqT, RespT> RespT blockingUnaryCall(MethodDescriptor<ReqT, RespT> method, ReqT req, int retry) {
        for (int i = 0; i < retry; i++) {
            try {
                return getBlockingStub().blockingUnaryCall(method, req);
            } catch (Exception e) {
                if (i == retry - 1) {
                    throw new RuntimeException("Failed after " + retry + " retries", e);
                }
                resetStub();
            }
        }
        return null;
    }

    public <ReqT, RespT> KVPair<Boolean, RespT> concurrentBlockingUnaryCall(
            MethodDescriptor<ReqT, RespT> method, ReqT req, Predicate<RespT> predicate) {
        List<Future<RespT>> futures = new ArrayList<>();
        List<String> hosts = new ArrayList<>(chs.keySet());
        for (String host : hosts) {
            ManagedChannel channel = connect(host);
            AbstractBlockingStub stub = createBlockingStub();
            Future<RespT> future = Executors.newSingleThreadExecutor().submit(() -> {
                try {
                    return stub.blockingUnaryCall(method, req);
                } catch (Exception e) {
                    return null;
                }
            });
            futures.add(future);
        }

        for (Future<RespT> future : futures) {
            try {
                RespT response = future.get(config.getTimeout(), TimeUnit.SECONDS);
                if (response != null && predicate.test(response)) {
                    return new KVPair<>(true, response);
                }
            } catch (Exception e) {
                // Handle timeout or exception
            }
        }
        return new KVPair<>(false, null);
    }

    public <ReqT, RespT> void streamingCall(
            MethodDescriptor<ReqT, RespT> method, ReqT request, StreamObserver<RespT> responseObserver, int retry) {
        for (int i = 0; i < retry; i++) {
            try {
                getStub().startCall(method, request, responseObserver);
                return;
            } catch (Exception e) {
                if (i == retry - 1) {
                    throw new RuntimeException("Failed after " + retry + " retries", e);
                }
                resetStub();
            }
        }
    }

    public void close() {
        for (ManagedChannel ch : chs.values()) {
            closeChannel(ch);
        }
        chs.clear();
    }

    protected void closeChannel(ManagedChannel channel) {
        if (channel != null && !channel.isShutdown()) {
            try {
                channel.shutdownNow().awaitTermination(5, TimeUnit.SECONDS);
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        }
    }

    public static class KVPair<K, V> {
        private final K key;
        private final V value;

        public KVPair(K key, V value) {
            this.key = key;
            this.value = value;
        }

        public K getKey() {
            return key;
        }

        public V getValue() {
            return value;
        }
    }

    protected static class AbstractClientStubProxy {
        private final AbstractClient client;

        public AbstractClientStubProxy(AbstractClient client) {
            this.client = client;
        }

        public AbstractBlockingStub getBlockingStub() {
            return client.stubs.computeIfAbsent("default", k -> client.createBlockingStub());
        }

        public AbstractStub getStub() {
            return client.createStub();
        }
    }
}

