class HttpProxyCacheServerClients {
    private final HttpProxyCacheServer server;
    private final String url;
    private final Map<String, byte[]> cache;

    public HttpProxyCacheServerClients(HttpProxyCacheServer server, String url) {
        this.server = server;
        this.url = url;
        this.cache = new HashMap<>();
    }

    public void processRequest(BufferedReader reader, OutputStream output, String url) {
        try {
            String line;
            StringBuilder request = new StringBuilder();
            while ((line = reader.readLine()) != null && !line.isEmpty()) {
                request.append(line).append("\r\n");
            }
            if (request.length() == 0) {
                server.closeSocketInput(reader);
                server.closeSocketOutput(output);
                server.closeSocket(reader);
                return;
            }
            byte[] cachedResponse = cache.get(url);
            if (cachedResponse != null) {
                output.write(cachedResponse);
                output.flush();
                return;
            }
            // Forward the request to the target server
            URL targetUrl = new URL(url);
            HttpURLConnection connection = (HttpURLConnection) targetUrl.openConnection();
            connection.setRequestMethod("GET");
            connection.setInstanceFollowRedirects(false);
            connection.connect();
            int responseCode = connection.getResponseCode();
            if (responseCode >= 200 && responseCode < 300) {
                InputStream inputStream = connection.getInputStream();
                ByteArrayOutputStream buffer = new ByteArrayOutputStream();
                byte[] dataBuffer = new byte[1024];
                int bytesRead;
                while ((bytesRead = inputStream.read(dataBuffer, 0, dataBuffer.length)) != -1) {
                    buffer.write(dataBuffer, 0, bytesRead);
                }
                byte[] responseData = buffer.toByteArray();
                cache.put(url, responseData);
                output.write(responseData);
                output.flush();
            } else {
                String errorResponse = "HTTP/1.1 " + responseCode + " " + connection.getResponseMessage() + "\r\n\r\n";
                output.write(errorResponse.getBytes());
                output.flush();
            }
        } catch (IOException e) {
            server.onError(e);
        } finally {
            server.releaseSocket(reader);
        }
    }
}

