import java.io.*;
import java.net.*;
import java.util.logging.*;

public class TcpTransport {
    protected ServerSocket srv_sock;
    protected Socket sock;
    protected OutputStream output;
    protected InputStream input;
    protected Thread receiver_thread;
    protected RtReceiver receiver;
    protected InetAddress host;
    protected int port = 7800;
    protected int out_buf_size = 8192;
    protected int in_buf_size = 8192;
    protected boolean server = false;
    protected boolean tcp_nodelay = false;
    protected Log log;

    public void start(String... options) {
        for (String opt : options) {
            if (opt.startsWith("server=")) {
                server = Boolean.parseBoolean(opt.substring(7));
            } else if (opt.startsWith("host=")) {
                try {
                    host = InetAddress.getByName(opt.substring(5));
                } catch (UnknownHostException e) {
                    log.severe("Invalid host: " + opt.substring(5));
                }
            } else if (opt.startsWith("port=")) {
                port = Integer.parseInt(opt.substring(5));
            } else if (opt.startsWith("out_buf_size=")) {
                out_buf_size = Integer.parseInt(opt.substring(13));
            } else if (opt.startsWith("in_buf_size=")) {
                in_buf_size = Integer.parseInt(opt.substring(12));
            } else if (opt.startsWith("tcp_nodelay=")) {
                tcp_nodelay = Boolean.parseBoolean(opt.substring(12));
            }
        }

        if (server) {
            try {
                srv_sock = new ServerSocket(port);
                log.info("Server started on port " + port);
                sock = srv_sock.accept();
                log.info("Client connected");
            } catch (IOException e) {
                log.severe("Server socket error: " + e.getMessage());
                return;
            }
        } else {
            if (host == null) {
                log.severe("Host not specified for client mode");
                return;
            }
            try {
                sock = new Socket(host, port);
                log.info("Connected to " + host.getHostAddress() + ":" + port);
            } catch (IOException e) {
                log.severe("Connection error: " + e.getMessage());
                return;
            }
        }

        try {
            output = sock.getOutputStream();
            input = sock.getInputStream();
            if (tcp_nodelay) {
                ((SocketImpl) sock.getImpl()).setOption(SocketOptions.TCP_NODELAY, true);
            }
        } catch (IOException e) {
            log.severe("Stream setup error: " + e.getMessage());
            return;
        }

        receiver_thread = new Receiver(input);
        receiver_thread.start();
    }

    public void stop() {
        try {
            if (receiver_thread != null && receiver_thread.isAlive()) {
                receiver_thread.interrupt();
            }
            if (input != null) input.close();
            if (output != null) output.close();
            if (sock != null) sock.close();
            if (srv_sock != null) srv_sock.close();
        } catch (IOException e) {
            log.severe("Stop error: " + e.getMessage());
        }
    }

    public void send(byte[] buf, int offset, int length) {
        try {
            output.write(buf, offset, length);
            output.flush();
        } catch (IOException e) {
            log.severe("Send error: " + e.getMessage());
        }
    }

    protected class Receiver extends Thread {
        private InputStream in;

        public Receiver(InputStream in) {
            this.in = in;
        }

        public void run() {
            byte[] buffer = new byte[in_buf_size];
            int bytesRead;
            try {
                while ((bytesRead = in.read(buffer)) != -1) {
                    if (receiver != null) {
                        receiver.receive(buffer, 0, bytesRead);
                    }
                }
            } catch (IOException e) {
                log.severe("Receiver error: " + e.getMessage());
            } finally {
                try {
                    in.close();
                } catch (IOException e) {
                    log.severe("Input stream close error: " + e.getMessage());
                }
            }
        }
    }
}

