        return uri;
    }

    public void destroy() {
        try {
            if (serverSocket != null && !serverSocket.isClosed()) {
                serverSocket.close();
            }
        } catch (IOException e) {
            System.err.println(TAG + ": Error closing server socket: " + e.getMessage());
        }
    }

    private Uri create(String filename, String mimeType) {
        ExecutorService executor = Executors.newSingleThreadExecutor();
        try {
            serverSocket = new ServerSocket(0);
            int port = serverSocket.getLocalPort();
            String hostAddress = InetAddress.getLocalHost().getHostAddress();
            String uriString = "http://" + hostAddress + ":" + port + "/stream";
            uri = new Uri(uriString);

            executor.execute(() -> {
                try {
                    while (!serverSocket.isClosed()) {
                        Socket clientSocket = serverSocket.accept();
                        new Thread(() -> handleClient(clientSocket, filename, mimeType)).start();
                    }
                } catch (IOException e) {
                    System.err.println(TAG + ": Server socket error: " + e.getMessage());
                }
            });
        } catch (IOException | URISyntaxException e) {
            System.err.println(TAG + ": Failed to create URI or server socket: " + e.getMessage());
        } finally {
            executor.shutdown();
        }
        return uri;
    }

    private void handleClient(Socket clientSocket, String filename, String mimeType) {
        try (InputStream fileStream = Files.newInputStream(Paths.get(filename));
